ROW.GRDJSDS_VALUE:
var ZXGFILE0 = &quot;&quot;;
var ZXGFILE1 = &quot;&quot;;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	var layoutxml=&quot;&lt;?xml version = &apos;1.0&apos; encoding=\&quot;GBK\&quot;?&gt; &quot;+
		&quot;&lt;ROWSET&gt;&quot;+
		&quot;&lt;ROW &gt;&quot;+
		 &quot;&lt;C0  height=\&quot;20%\&quot; width=\&quot;50%\&quot; ROWSPAN=\&quot;1\&quot; COLSPAN=\&quot;1\&quot;&gt;2&lt;/C0&gt;&quot;+
		 &quot;&lt;C1  height=\&quot;20%\&quot; width=\&quot;50%\&quot; ROWSPAN=\&quot;1\&quot; COLSPAN=\&quot;1\&quot;&gt;3&lt;/C1&gt;&quot;+
		&quot;&lt;/ROW&gt;&quot;+
		&quot;&lt;ROW &gt;&quot;+
		 &quot;&lt;C0  height=\&quot;80%\&quot; width=\&quot;50%\&quot; ROWSPAN=\&quot;1\&quot; COLSPAN=\&quot;1\&quot;&gt;0&lt;/C0&gt;&quot;+
		 &quot;&lt;C1  height=\&quot;80%\&quot; width=\&quot;50%\&quot; ROWSPAN=\&quot;1\&quot; COLSPAN=\&quot;1\&quot;&gt;1&lt;/C1&gt;&quot;+
		&quot;&lt;/ROW&gt;&quot;+
		&quot;&lt;/ROWSET&gt;&quot;;
	_this.SetLayoutDS(layoutxml);

	var yymmList = _this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_YYYY_MM&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	_this.SetToComboBox(&quot;&quot;,2,2,1,yymmList);
	_this.SetToComboBox(&quot;&quot;,2,2,3,yymmList);
	_this.SetToComboBox(&quot;&quot;,3,2,1,yymmList);
	_this.SetToComboBox(&quot;&quot;,3,2,3,yymmList);

	_this.SetCellText(2,2,1,G_LOGDAT.substring(0,5)+&quot;01&quot;);
	_this.SetCellText(2,2,3,G_LOGDAT.substring(0,7));
	_this.SetCellText(3,2,1,G_LOGDAT.substring(0,5)+&quot;01&quot;);
	_this.SetCellText(3,2,3,G_LOGDAT.substring(0,7));

	ZXGFILE0 = _this.SaveTempZXGFile(0);
	ZXGFILE1 = _this.SaveTempZXGFile(1);

	loadJYPTXM();
	loadBYDJXM();

}

function loadJYPTXM()
{
	_this.LoadZXGFile(ZXGFILE0,-1,0);
	_this.SetFixedRow(0,2);
	_this.SetColVisible(0,4,-1);
	_this.SetColVisible(0,5,-1);
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW);
	_this.SetMainCellRange(0,2,2,4,_this.GetColCount(0)-1);	

	var ym1 = _this.GetCellText(2,2,1);
	var ym2 = _this.GetCellText(2,2,3);
	var xmmc = _this.GetCellText(2,4,1);
	var xml = _sql.query(&quot;MAIN1&quot;,&quot;YM1=&quot;+ym1+&quot;&YM2=&quot;+ym2+&quot;&XMMC=&quot;+xmmc);
	_this.SetXmlDS(0,2,2,4,_this.GetColCount(0)-1,xml);

	var row = 0;
	for (var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++) {
		if (_this.GetCellText(0,r,2) != &quot;&quot;) {
			_this.SetToBoolBox(0,r,0);
			row ++;
			_this.SetCellText(0,r,1,row);
		}
	}

	_this.AutoFixScreenWidth();
}

function loadBYDJXM()
{
	_this.LoadZXGFile(ZXGFILE1,-1,1);
	_this.SetFixedRow(1,2);
	_this.SetColVisible(1,5,-1);
	_this.SetAttribe(&quot;SHEET_1&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW);
	_this.SetMainCellRange(1,2,2,4,_this.GetColCount(0)-1);	

	var ym1 = _this.GetCellText(3,2,1);
	var ym2 = _this.GetCellText(3,2,3);
	var xmmc = _this.GetCellText(3,4,1);
	var xml = _sql.query(&quot;MAIN2&quot;,&quot;YM1=&quot;+ym1+&quot;&YM2=&quot;+ym2+&quot;&XMMC=&quot;+xmmc);
	_this.SetXmlDS(1,2,2,4,_this.GetColCount(0)-1,xml);

	var row = 0;
	for (var r=_this.GetMainCellRangeRow1(1);r&lt;=_this.GetMainCellRangeRow2(1);r++) {
		if (_this.GetCellText(1,r,2) != &quot;&quot;) {
			_this.SetToBoolBox(1,r,0);
			row ++;
			_this.SetCellText(1,r,1,row);
		}
	}
	_this.AutoFixScreenWidth();
}



//匹配确认
function xmppCheck()
{
	var fgwxmuuid = &quot;&quot;;
	var fgwxmrow = -1;
	for (var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++) {
		if (_this.GetCellText(0,r,0) == &quot;1&quot;) {
			fgwxmuuid = _this.GetCellText(0,r,4);
			fgwxmrow = r;
			break;
		}
	}
	if (fgwxmuuid == &quot;&quot;) {
		alert(&quot;请选择发改委项目！&quot;);
		return;
	}

	var bydjuuid = &quot;&quot;;
	for (var r=_this.GetMainCellRangeRow1(1);r&lt;=_this.GetMainCellRangeRow2(1);r++) {
		if (_this.GetCellText(1,r,0) == &quot;1&quot;) {
			if (bydjuuid == &quot;&quot;) bydjuuid += _this.GetCellText(1,r,5);
			else bydjuuid += &quot;,&quot;+_this.GetCellText(1,r,5);
		}
	}
	if (bydjuuid == &quot;&quot;) {
//		alert(&quot;请选择已报验登记项目！&quot;);
//		return;
	}

	var param = new Object();
	param.fgwxmuuid = fgwxmuuid;
	param.bydjuuid = bydjuuid;
	var ret = _this.invokeOSFuncExt(&quot;xmppCheck&quot;,param);
	if (ret &gt;= 0) {
		alert(&quot;保存成功！&quot;);	
		_this.SetCellText(0,fgwxmrow,5,bydjuuid);
		loadBYDJXM();	
	}
	else {
		alert(&quot;保存失败：&quot;+ret);
	}
}

function selectOneRow(sheet,row,val) 
{
	for (var r=_this.GetMainCellRangeRow1(sheet);r&lt;=_this.GetMainCellRangeRow2(sheet);r++) {
		if (_this.GetCellText(sheet,r,1) != &quot;&quot;) {
			_this.SetCellText(sheet,r,0,&quot;0&quot;);
		}
	}
	_this.SetCellText(sheet,row,0,val);
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (sheet == 0) { //发改委项目是单选
		selectOneRow(sheet,row,newvalue);
	}
}

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if (id == &quot;查询&quot;) {
		if (sheet == 2) loadJYPTXM();
		if (sheet == 3) loadBYDJXM();
	}
}

//鼠标左键点击
function _thisOnMouseLClick(sheet,row,col)
{
	if (sheet == 0 && row &gt;= _this.GetMainCellRangeRow1(sheet) && row &lt;= _this.GetMainCellRangeRow2(sheet)) {
		selectOneRow(sheet,row,&quot;1&quot;);
		var bydjuuid = _this.GetCellText(0,row,5);
		for (var r=_this.GetMainCellRangeRow1(1);r&lt;=_this.GetMainCellRangeRow2(1);r++) {
			var byid = _this.GetCellText(1,r,5);
			if ( bydjuuid.indexOf(byid) &gt; -1 ) {
				_this.SetCellText(1,r,0,&quot;1&quot;);
				scrollToRow(1,r);
			}
			else if (_this.GetCellText(1,r,2) != &quot;&quot;) {
				_this.SetCellText(1,r,0,&quot;0&quot;);
			}
		}

	}
}

//滚动到某一行
function scrollToRow(sheet,row)
{
	var pos = -60;
	for (var r=0;r&lt;row;r++) {
		pos += _this.GetRowHeight(sheet,r);
	}
	_this.ScrollToPos(sheet,pos,0);
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

function xmppCheck()
{
	var db = null;
	var ret = 0;
	try {
		db = new pub.EADatabase();
		var sql = &quot;delete from tax_zdxm_fgwbyxm_dzb where fgwxmuuid=&apos;&quot;+fgwxmuuid+&quot;&apos;&quot;;
		db.ExcecutSQL(sql);

		sql = &quot;insert into tax_zdxm_fgwbyxm_dzb(fgwxmuuid,djxh,bydjuuid)
			select &apos;&quot;+fgwxmuuid+&quot;&apos; fgwxmuuid,djxh,bydjuuid from dj_wbnsrjydbydj where 
			bydjuuid in (&quot;+pub.EAFunc.SQLIN(bydjuuid)+&quot;)&quot;;
		ret = db.ExcecutSQL(sql);

		db.Commit();
		return ret;		
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}

ROW.DATDSC:
select a.xmmc,a.xmdm,a.fgwxmuuid,wm_concat(b.bydjuuid) bydjuuid
from tax_zdxm_fgwxm a,tax_zdxm_fgwbyxm_dzb b
where a.fgwxmuuid=b.fgwxmuuid(+)
  and a.xmdm not in (
      select a.fgwxmdm from tax_zdxm_jyptxm a,tax_zdxm_jyptwbbyxm_dzb b where a.jyptxmuuid=b.jyptxmuuid and a.fgwxmdm is not null
  )
  and to_char(to_date(jhkgsj,&apos;yyyy.mm&apos;),&apos;yyyy-mm&apos;)&gt;=&apos;[%YM1]&apos;
  and to_char(to_date(jhkgsj,&apos;yyyy.mm&apos;),&apos;yyyy-mm&apos;)&lt;=&apos;[%YM2]&apos;
  and xmmc like &apos;%[%XMMC]%&apos;
group by a.xmmc,a.xmdm,a.fgwxmuuid  
order by a.xmmc,a.xmdm,a.fgwxmuuid

select b.xmmc,b.xmje,a.wcjyhdssglzmbh,b.bydjuuid
from dj_wbnsrjydbydj a,(
select bydjuuid,djxh,wcjyhwmc xmmc,wcjyhwzz xmje,
	wcjyhwyxqxq dat1,wcjyhwyxqxz dat2
from dj_wbnsrjydbydj_hwxx
union all
select bydjuuid,djxh,wcjylwmc xmmc,wcjylwdhtje xmje,
	wcjylwyxqxq dat1,wcjylwyxqxz dat2 
from dj_wbnsrjydbydj_lwxx
) b where a.bydjuuid=b.bydjuuid
  and to_char(dat1,&apos;yyyy-mm-dd&apos;)&gt;=&apos;[%YM1]&apos;
  and to_char(dat2,&apos;yyyy-mm-dd&apos;)&lt;=&apos;[%YM2]&apos;
  and b.xmmc like &apos;%[%XMMC]%&apos;
order by a.wcjyhdssglzmbh
