ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW);
	_this.SetCellText(0,1,3,YM1+&quot;至&quot;+YM2);

	var xml = showModalDialog(&quot;show.sp?grdid=Waiting&quot;,window,&quot;scroll:no;status:no;dialogWidth=200px;dialogHeight=80px&quot;);
	_this.SetMainCellRange(0,3,1,5,15);
	_this.SetXmlDS(0,3,1,5,15,xml);

//	_this.SetToBoolBox(0,3,0);
	for (var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++) {
		//_this.SetToBoolBox(0,r,0);
		var rn = _this.GetCellText(0,r,6);
		if (rn != &quot;1&quot;) {
			_this.SetCellText(0,r,2,&quot;&quot;);
			_this.SetCellText(0,r,3,&quot;&quot;);
			_this.SetCellText(0,r,4,&quot;&quot;);
			_this.SetCellText(0,r,5,&quot;&quot;);
			//_this.MergeCells(0,r,2,r,5);
		}
	}

	//_this.AutoFixColWidth(0,300);
	_this.AutoFixScreenWidth();	


}

function runWaitingTask()
{
	try {
		var xml = _sql.query(&quot;MAIN&quot;,&quot;YM1=&quot;+YM1+&quot;&YM2=&quot;+YM2+&quot;&ZDBZ=&quot;+ZDBZ);
		return xml;
	}catch(e) { alert(e.toString()); }
}

ROW.DATDSC:
select rownum,t.* from (
select a.jhkgsj,a.xmdm,a.xmmc,a.ztzje,
     row_number() over (partition by a.xmdm,a.xmmc order by a.xmdm,a.xmmc) rn,
       b.xmmc jyptxmmc,b.tjyf,b.jyje,
       case when b.fgwxmdm is null then &apos;否&apos; else &apos;是&apos; end &quot;已招投标否？&quot;,
       case when dzb.bydjuuid is null then &apos;否&apos; else &apos;是&apos; end &quot;税务登记否？&quot;,
       case when sb.bydjuuid is null then &apos;否&apos; else &apos;是&apos; end &quot;税务申报否？&quot;,
       sb.sbje &quot;申报金额&quot;,
       sb.jnsk &quot;缴纳税款&quot;,
       sb.yyjskje &quot;已预缴税款金额&quot;
from tax_zdxm_fgwxm a,tax_zdxm_jyptxm b,tax_zdxm_jyptwbbyxm_dzb dzb,
     (
        select sb.bydjuuid,sb.wcjyhdssglzmbh,sb.sjjyqjq,sb.sjjyqjz,sb.bysj,sb.wcjyhdqksbuuid,
               sbxx.sbje,sbxx.jnsk,sbxx.yyjskje
        from dj_wcjyhdqksbbxx sb,(
               select wcjyhdqksbuuid,sum(sbje) sbje,sum(jnsk) jnsk,sum(yyjskje) yyjskje from (
		       select wcjyhdqksbuuid,wcjyhwmc,hwxse sbje,xshwjnsk jnsk,yyjskje from dj_wcjyhdqksbbxx_hwxx
		       union all
		       select wcjyhdqksbuuid,wcjylwmc,yslwyye sbje,yslwjnsk jnsk,0 yyjskje from dj_wcjyhdqksbbxx_lwxx sblw
		    ) group by wcjyhdqksbuuid
             ) sbxx
        where sb.wcjyhdqksbuuid=sbxx.wcjyhdqksbuuid 
          and sb.zfbz_1=&apos;N&apos;          
     ) sb
where a.xmdm=b.fgwxmdm(+)
  and b.jyptxmuuid=dzb.jyptxmuuid(+)
  and dzb.bydjuuid=sb.bydjuuid(+)  
  and to_char(to_date(a.jhkgsj,&apos;yyyy.mm&apos;),&apos;yyyy-mm&apos;)&gt;=&apos;[%YM1]&apos;
  and to_char(to_date(a.jhkgsj,&apos;yyyy.mm&apos;),&apos;yyyy-mm&apos;)&lt;=&apos;[%YM2]&apos;
  and a.zdbz like &apos;[%ZDBZ]%&apos;
order by a.xmdm
) t
