ROW.GRDJSDS_VALUE:
var yyyy = &quot;&quot;;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	try {
		yyyy = YYYY;
		if (yyyy == &quot;&quot;) yyyy = G_LOGDAT.substring(0,4);
	}
	catch(e){
		yyyy = G_LOGDAT.substring(0,4);
		_this.ShowToolBar(0);
	}
	_this.SetCellText(sheet,1,1,yyyy+&quot;年税收情况分析表&quot;);

	init(sheet);

	_this.SplitSheet(sheet,&quot;V&quot;,&quot;40%&quot;);
	showChar(1);
}

function init(sheet)
{
	for (var i=2;i&lt;2+12;i++) {
		var mnystr = _this.GetCellText(sheet,4,i);
		if (mnystr != &quot;&quot;) {
			var m = mnystr.split(&quot;_&quot;);
			_this.SetCellText(sheet,4,i,m[0]);
			_this.SetCellText(sheet,5,i,m[1]);
		}
	}
	_this.RefreshFormula();
}

function showChar(sheet) 
{
	var xml =getxml();
	//_this.SetCellText(0,2,3,xml);

	var title = yyyy + &quot;年税收情况分析表&quot;;
	var xtitle = &quot;月份&quot;;
	var ytitle = &quot;金额&quot;;
	var chart = new SVGChart(&quot;CHART1&quot;,sheet,-1,-1,0,title,xtitle,ytitle,860,300,xml );
	chart.m_bShowSeralName = 1;

	chart.load();
}

function getxml()
{
	var xml = &quot;&lt;?xml version = &apos;1.0&apos;?&gt;&lt;ROWSET&gt;&quot;;
	for (var c=2;c&lt;14;c++) {
		xml += &quot;&lt;ROW num=\&quot;&quot;+(c-1)+&quot;\&quot; name=\&quot;&quot;+_this.GetCellText(0,3,c)+&quot;\&quot;&gt;\n&quot;;
		xml += &quot;&lt;增值税&gt;&quot;+_this.GetCellText(0,4,c)+&quot;&lt;/增值税&gt;\n&quot;;
		xml += &quot;&lt;所得税&gt;&quot;+_this.GetCellText(0,5,c)+&quot;&lt;/所得税&gt;\n&quot;;
		xml += &quot;&lt;/ROW&gt;\n&quot;;
	}
	xml += &quot;&lt;/ROWSET&gt;&quot;;

	return xml;

}
