ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SplitSheet(sheet,&quot;V&quot;,&quot;50%&quot;);
	cal();
	showChar(1);
}

function init(sheet)
{
	for (var i=2;i&lt;2+12;i++) {
		var mnystr = _this.GetCellText(sheet,4,i);
		if (mnystr != &quot;&quot;) {
			var m = mnystr.split(&quot;_&quot;);
			_this.SetCellText(sheet,4,i,m[0]);
			_this.SetCellText(sheet,5,i,m[1]);
		}
	}
	_this.RefreshFormula();
}

function showChar(sheet) 
{
	var xml = getxml();
	var title = YYYY + &quot;税收年度环比&quot;;
	var xtitle = &quot;月份&quot;;
	var ytitle = &quot;金额&quot;;
	var chart = new SVGChart(&quot;CHART1&quot;,sheet,-1,-1,1,title,xtitle,ytitle,1000,300,xml );
	chart.m_bShowSeralName = 1;

	chart.load();
}

function getxml()
{
	var xml = &quot;&lt;?xml version = &apos;1.0&apos;?&gt;&lt;ROWSET&gt;&quot;;
	for (var r=4;r&lt;=_this.GetMainCellRangeRow2(0);r++) {
		xml += &quot;&lt;ROW num=\&quot;&quot;+(r-3)+&quot;\&quot; name=\&quot;&quot;+_this.GetCellText(0,r,1)+&quot;\&quot;&gt;\n&quot;;
		xml += &quot;&lt;本月缴纳税额&gt;&quot;+_this.GetCellText(0,r,2)+&quot;&lt;/本月缴纳税额&gt;\n&quot;;
		//xml += &quot;&lt;环比增长&gt;&quot;+(1*_this.GetCellText(0,r,3))*_this.GetCellText(0,r-1,2)+&quot;&lt;/环比增长&gt;\n&quot;;
		xml += &quot;&lt;环比增长&gt;&quot;+_this.GetCellText(0,r,4)+&quot;&lt;/环比增长&gt;\n&quot;;
		xml += &quot;&lt;/ROW&gt;\n&quot;;
	}
	xml += &quot;&lt;/ROWSET&gt;&quot;;
	return xml;

}


//计算增长率
function cal()
{
	var old = 0.0;
	for(var i = 4;i&lt; _this.GetRowCount(0)-1;i++){
		var cur = 1.0*_this.GetCellText(0,i,2);
		//if ( _this.GetCellText(0,i,2)==&quot;&quot;||_this.GetCellText(0,i,2)==&quot;0&quot; ) break;
		if ( old!=0 ) {
			var val = (cur-old)/old;
			_this.SetCellText(0,i,3,convertRound(val,2) );
			_this.SetCellText(0,i,4,convertRound(cur-old,2) );
		}
		old= cur;
	}
}

function convertRound(ADoubleInput,ADotNumber){
    var tmpValue1=1;
    var tmpValue2=1;
    if (ADotNumber&lt;0) return ADoubleInput;
    if (0==ADoubleInput) return Math.round(ADoubleInput);
    for (i=0;i&lt;ADotNumber;i++){
      tmpValue1=tmpValue1*10;
    }
    tmpValue2=ADoubleInput*tmpValue1;
    return Math.round(tmpValue2)/tmpValue1;
  }

//test
// ***************************************************************************
// SVGChart(objid,sheet,row,col,type,title,xtitle,ytitle,width,height,xml)
// objid: SetToSvgform 的Object ID编号
// sheet,row,col, 图表所在的表格位置
// type: =0 线图 =1 直方图 =2 立方图　=3 饼图
// title: 标题 xtitle x轴名称 ytitle y轴名称
// width,height: 要求固定的高度和宽度，如果是自动适应窗口的大小，设置为-1，-1
// xml: xmlds描述的数据字符串
// ***************************************************************************
function SVGChart(objid,sheet,row,col,type,title,xtitle,ytitle,width,height,xml)
{
	this.m_objid = objid;
	this.m_sheet = sheet;
	this.m_row = row;
	this.m_col = col;
	this.m_width  = width;
	this.m_height = height;  
	this.m_title = title;
	this.xml = xml;
	this.m_xtitle=xtitle;
	this.m_ytitle=ytitle;
	this.m_type = type;

	// 系列的颜色列表如果超过循环
	this.m_color = new Array(&quot;#00cc99&quot;,&quot;#C2CC0D&quot;,&quot;#71CC16&quot;,&quot;#FFCCFF&quot;,&quot;#CC68CC&quot;,&quot;#66FFCC&quot;,&quot;#DD0000&quot;,&quot;#FF9966&quot;,&quot;#C2A00D&quot;,&quot;#71A216&quot;,&quot;#66FFCC&quot;);	
	this.m_color1 = new Array(&quot;#0D767E&quot;,&quot;#999966&quot;,&quot;#006600&quot;,&quot;#CC6733&quot;,&quot;#CC6744&quot;,&quot;#669999&quot;,&quot;#880000&quot;,&quot;#FF6600&quot;,&quot;#CC6733&quot;,&quot;#666633&quot;,&quot;#66FF99&quot;);	
	this.m_bacolor = &quot;#F0F0F7&quot;;	// 背景颜色
	this.m_fkcolor = &quot;#DDE6EF&quot;;	// 前景颜色
	this.m_titlefontname = lang_svgchart.fontsong;  //&quot;宋体&quot;;	// 标题字体
	this.m_titlefontsize = 14;	// 标题字体大小	
	this.m_xtitlefontname = &quot;Arial&quot;;	// X标题字体
	this.m_xtitlefontsize = 12;	// X标题字体大小	
	this.m_ytitlefontname = &quot;Arial&quot;;	// Y标题字体
	this.m_ytitlefontsize = 12;	// Y标题字体大小	
	this.m_xscalefontname = &quot;Arial&quot;;	// X刻度字体
	this.m_xscalefontsize = 12;	// X刻度字体大小	
	this.m_yscalefontname = &quot;Arial&quot;;	// Y刻度字体
	this.m_yscalefontsize = 9;	// Y刻度字体大小	
	this.m_main_x = 100;		// 主图的左边位置
	this.m_main_y = 100;		// 主图的左边位置
	this.m_ysacle = 10;		// y轴保留的刻度个数
	this.m_titlex = 5;		// 标题的Y位置
	this.m_ymulti = 1;		// 坐标轴的被除数，比如要显示　万元那么设为１００００
	this.m_xsubstrleft = 0;		// X坐标轴的从左开始截取多少个字符，一般用于仅显示部分字符
	this.m_xsubstrright = 0;		// X坐标轴的从右开始截取多少个字符，比如为2，那么2007-01-03则显示03
	this.PieSelectCol = -1;		// 饼图的当前选择列，一个饼图之显示一个列
	this.m_bShowLinePoint = 0 ;		// 在线图是否显示点
	this.m_bShowSeralName = 0 ;		// 是否在右边显示系列名称
	this.m_bSeralNamePercent = 0.2 ;	// 系列名称占宽度的百分比,如果系列名称比较长，建议调大
	this.m_maxhscalenum = 40 ;       // 横坐标最大刻度个数，一般用于当横坐标很多时候,如横坐标共150,m_maxhscalenum=30,那么每隔5个显示一个横坐标

	_this.XMLDS_Reset();
	_this.XMLDS_Parse(this.xml);

	this.load = svgchart_load;			// 重新载入
	this.hidecol = svgchart_hidcol;			// 在载入的DS中不显示某一列，一般用于表格的某一列数据不进入某个系列
}

function svgchart_hidcol(col) 
{
	_this.XMLDS_DeleteColumn(col);
}

// ***************************************************************************
// 解析重新生成svgchar
// ***************************************************************************
function svgchart_load()
{
	if ( _this.GetReleaseNo()&lt;&quot;4.42&quot;) {
		//alert ( &quot;要正确显示图表，要求您的控件在4.42以上，请升级您的硕格控件&quot; );
    alert(lang_svgchart.ctlversion);
		return;
	}
	var width = this.m_width;
	var height = this.m_height;
	_this.SetToSVGForm(this.m_objid ,this.m_sheet,this.m_row,this.m_col,&quot;&quot;);
	if ( width == -1 ) width  = _this.GetObjectWidth(this.m_objid);
	if ( height == -1 ) height = _this.GetObjectHeight(this.m_objid);  
	var x1 = this.m_main_x;
	if ( x1 &gt; width /10 ) x1 = width/10;
	var y1 = this.m_main_y ;
	if ( y1 &gt; height /10 ) y1 = height/10;
	var x2 = 1*(width-x1-this.m_bShowSeralName*width *this.m_bSeralNamePercent);// 

	var y2 = 1*(height-2*y1);
	// 根据xmlds得到一个svg图的xml字符串
	var svg= &quot;&quot;;
	svg = &quot;&lt;?xml version=\&quot;1.0\&quot; encoding=\&quot;GBK\&quot;?&gt;&lt;svg  &gt; &quot;;//bgcolor=\&quot;#D3E5FA\&quot; 

	// 背景
//	svg+= &quot;&lt;path fill=\&quot;&quot;+this.m_fkcolor+&quot;\&quot; opacity=\&quot;0.1\&quot; d=\&quot;M148.9,261l305.6-58.5l152.8,117l-305.6,58.5L148.9,261z\&quot;/&gt;&quot;;
//	svg+= &quot;&lt;path fill=\&quot;&quot;+this.m_fkcolor+&quot;\&quot; opacity=\&quot;0.1\&quot; style=\&quot;&st72;\&quot; d=\&quot;M148.9,110v151l305.6-58.5v-151L148.9,110z\&quot;/&gt;&quot;;
//	svg+= &quot;&lt;path fill=\&quot;&quot;+this.m_fkcolor+&quot;\&quot; opacity=\&quot;0.1\&quot; style=\&quot;&st54;\&quot; d=\&quot;M481.9,72.5l-27.5-21v151l152.8,117v-151l-125.4-96z\&quot;/&gt;&quot;;

	var rowcount = _this.XMLDS_GetRowCount();
	var colcount = _this.XMLDS_GetColumnCount();
	var max = 0 ;// 最大的真实值
	var min = 0 ;// 最小的真实值
	for ( var i=0;i&lt;_this.XMLDS_GetRowCount();i++ ) {
		for ( var j=0;j&lt;_this.XMLDS_GetColumnCount();j++ ) {
			var val = _this.XMLDS_GetStringAt(i,j);
			if ( 1.0*val&gt;max ) max = 1.0*val;
			if ( 1.0*val&lt;min ) min = 1.0*val;
		}	
	}
	max = max*1.1;

	if( this.m_type==&quot;0&quot;||this.m_type==&quot;1&quot;||this.m_type==&quot;2&quot; ){ // 坐标类
		svg+= &quot;&lt;rect x=\&quot;&quot;+x1+&quot;\&quot;  y=\&quot;&quot;+y1+&quot;\&quot; width=\&quot;&quot;+(x2-x1)+&quot;\&quot; height=\&quot;&quot;+(y2-y1)+&quot;\&quot; fill=\&quot;&quot;+this.m_bacolor+&quot;\&quot; /&gt;&quot;; //fill=\&quot;&quot;+this.m_bacolor+&quot;\&quot;  opacity=\&quot;0.1\&quot; 
		// X 轴线	
		svg+= &quot;&lt;line x1=\&quot;&quot;+x1+&quot;\&quot; y1=\&quot;&quot;+y2+&quot;\&quot; x2=\&quot;&quot;+(x2+12)+&quot;\&quot; y2=\&quot;&quot;+y2+&quot;\&quot; endcap=\&quot;8\&quot; stroke-width=\&quot;1.2\&quot;&gt;&lt;/line&gt;&quot;; 	
		// y 轴线	
		svg+= &quot;&lt;line x1=\&quot;&quot;+x1+&quot;\&quot; y1=\&quot;&quot;+y2+&quot;\&quot; x2=\&quot;&quot;+x1+&quot;\&quot; y2=\&quot;&quot;+(y1-12)+&quot;\&quot; endcap=\&quot;8\&quot; stroke-width=\&quot;1.2\&quot;&gt;&lt;/line&gt;&quot;; 	
		var nMax = max ;// 最大的刻度值 
		var nMin = 0 ;// 最小的刻度值
		var tickSpace = 0 ;// 每个刻度值
		if ( max &gt; 0 ) {
			var ss= &quot;&quot;+ Math.floor(max);
			var nnn = 5* Math.pow(this.m_ysacle,ss.length-2);;
			var n = 0;
			if (nnn!=0 ) n=nnn - nMax%(nnn);
			nMax += n;	
			if ( nMax&gt;=0 && nMax &lt; this.m_ysacle ) nMax = this.m_ysacle;
			tickSpace = nMax/this.m_ysacle;
			}
		else {//  负数支持？
			tickSpace = nMax/this.m_ysacle;
			nMin = nMax; nMax= 0;
		}
		// y轴刻度		
		var oneheight = (y2-y1)/this.m_ysacle;
		for ( var i=0;i&lt;=this.m_ysacle;i++ ){
			var y = y1+i*oneheight;
			if ( i &gt; 0 && i&lt;this.m_ysacle ) {
				svg+= &quot;&lt;line x1=\&quot;&quot;+(x1-2)+&quot;\&quot; y1=\&quot;&quot;+y+&quot;\&quot; x2=\&quot;&quot;+(x1+2)+&quot;\&quot; y2=\&quot;&quot;+y+&quot;\&quot;&gt;&lt;/line&gt;&quot;; 	
				svg+= &quot;&lt;line x1=\&quot;&quot;+(x1+2)+&quot;\&quot; y1=\&quot;&quot;+y+&quot;\&quot; x2=\&quot;&quot;+x2+&quot;\&quot; y2=\&quot;&quot;+y+&quot;\&quot; linestyle=\&quot;1\&quot; stroke=\&quot;#CCCCCC\&quot;&gt;&lt;/line&gt;&quot;; 	
			}
			svg+= &quot;&lt;text x=\&quot;1\&quot; y=\&quot;&quot;+(y-6)+&quot;\&quot; width=\&quot;&quot;+x1+&quot;\&quot; height=\&quot;&quot;+oneheight+&quot;\&quot; align=\&quot;right\&quot; font-farmily=\&quot;&quot;+this.m_yscalefontname+&quot;\&quot; font-size=\&quot;&quot;+this.m_yscalefontsize+&quot;\&quot;&gt;&quot;+1*((tickSpace *(this.m_ysacle-i))/this.m_ymulti)+&quot;&lt;/text&gt;&quot;;
		}
		// x轴刻度
		var onewidth = 1.0*(x2-x1)/(rowcount+1);
		var x_width = onewidth;
		var bMaxhscalenum = -1;

		if ( this.m_maxhscalenum != -1 && rowcount&gt;=this.m_maxhscalenum ) {
			bMaxhscalenum  = Math.ceil(rowcount/this.m_maxhscalenum) ;
			x_width = 1.0*(x2-x1)/(this.m_maxhscalenum+1);

		}
		for( var r=0;r&lt;rowcount;r++){
			var label = _this.XMLDS_GetRowName(r);
			if ( label.length==0 ) label = _this.XMLDS_GetStringAt(r,0);
			if ( this.m_xsubstrleft != 0 ) label = label.substring(0,this.m_xsubstrleft);
			if ( this.m_xsubstrright != 0 ) label = label.substring(label.length-this.m_xsubstrright,label.length);

			svg+= &quot;&lt;line x1=\&quot;&quot;+(x1+(r+1)*onewidth )+&quot;\&quot; y1=\&quot;&quot;+(y2-2)+&quot;\&quot; x2=\&quot;&quot;+(x1+(r+1)*onewidth )+&quot;\&quot; y2=\&quot;&quot;+(y2+2)+&quot;\&quot;&gt;&lt;/line&gt;&quot;;  

			// 决定显示多少个刻度
			if ( (bMaxhscalenum !=-1 &&r%bMaxhscalenum==0)||bMaxhscalenum ==-1  ) {
				svg+= &quot;&lt;text x=\&quot;&quot;+(x1+(r+1)*onewidth -onewidth/2)+&quot;\&quot; y=\&quot;&quot;+(y2+5)+&quot;\&quot; width=\&quot;&quot;+x_width+&quot;\&quot; height=\&quot;&quot;+(height-y2-5)+&quot;\&quot; align=\&quot;center\&quot; font-farmily=\&quot;&quot;+this.m_xscalefontname+&quot;\&quot; font-size=\&quot;&quot;+this.m_xscalefontsize+&quot;\&quot;&gt;&quot;+label+&quot;&lt;/text&gt;&quot;;
			}

		}
		if ( this.m_type == &quot;0&quot; ) {// 显示线图
			var point = &quot;&quot;;
			for ( var c=0;c&lt;colcount;c++ ) {
				svg += &quot;&lt;polyline stroke=\&quot;&quot;+this.m_color[c%this.m_color.length]+&quot;\&quot;  points=\&quot;&quot;;
				for ( var r=0;r&lt;rowcount;r++ ){
					var x = 1*(x1+(r+1)*onewidth) ;
					var val = 1*_this.XMLDS_GetStringAt(r,c);
					if ( isNaN(val ) ) val  = 0 ;

					var y = y2-1*(val*(y2-y1)/nMax);
					svg += &quot;&quot;+Math.floor(x)+&quot;,&quot;+ Math.floor(y)+&quot; &quot;;
					//2010修改，如果需要显示点，并且线条是白色
					if(this.m_color[c%this.m_color.length]==&quot;#FFFFFF&quot; )
						point+=&quot;&lt;rect fill=\&quot;#FF0000\&quot; x=\&quot;&quot;+(x-2)+&quot;\&quot;  y=\&quot;&quot;+(y-2)+&quot;\&quot; width=\&quot;4\&quot; height=\&quot;4\&quot; tip=\&quot;&quot;+val +&quot;\&quot; /&gt;&quot;;

				}
				svg+=&quot;\&quot;/&gt;&quot;;

			}
			svg+=point;
//			if ( this.m_bShowLinePoint == 1 ) // 在线图是否显示点
//				svg+=point;		
		}
		else if ( this.m_type == &quot;1&quot; ||this.m_type == &quot;2&quot; ) {// 显示 直方图 
				for ( var r=0;r&lt;rowcount;r++ ){
				//svg += &quot;&lt;polyline stroke=\&quot;&quot;+this.m_color[c%this.m_color.length]+&quot;\&quot; points=\&quot;&quot;;
				var p = 0.5;//+colcount*0.1;//空歇比率
				if ( p&gt;0.8 )p=0.8;// 
				var Margin = onewidth*p;
				var startloc = 1*(x1+(r+1)*onewidth) - onewidth/2 +Margin /2;
				var percolwidth = (onewidth-Margin )/colcount;
				for ( var c=0;c&lt;colcount;c++ ) {
					var s =1*_this.XMLDS_GetStringAt(r,c);
					if ( isNaN(s) ) s = 0 ;
					var h = y2-(s*(y2-y1)/nMax);
					svg += &quot;&lt;rect fill=\&quot;&quot;+this.m_color[c%this.m_color.length]+&quot;\&quot; x=\&quot;&quot;+(startloc+c*percolwidth )+&quot;\&quot; y=\&quot;&quot;+h+&quot;\&quot; width=\&quot;&quot;+percolwidth +&quot;\&quot; height=\&quot;&quot;+(y2-h)+&quot;\&quot; tip=\&quot;&quot;+s+&quot;\&quot; /&gt;&quot;;
					//alert ( &quot;&lt;rect fill=\&quot;&quot;+this.m_color[c%this.m_color.length]+&quot;\&quot; x=\&quot;&quot;+(startloc+c*percolwidth )+&quot;\&quot; y=\&quot;&quot;+h+&quot;\&quot; width=\&quot;&quot;+percolwidth +&quot;\&quot; height=\&quot;&quot;+(y2-h)+&quot;\&quot; /&gt;&quot; );
					if ( this.m_type == &quot;2&quot; ){	//立方图
						var nlet = 1*percolwidth/2; // 斜角计算的
						if ( h!= y2 ) {
              //后背
							svg += &quot;&lt;polygon fill=\&quot;&quot;+this.m_color1[c%this.m_color.length]+&quot;\&quot;  points=\&quot;&quot;;
							svg +=(startloc+c*percolwidth+percolwidth-1)+&quot;,&quot;+(h)+&quot; &quot;;
							svg +=(startloc+c*percolwidth+percolwidth-1)+&quot;,&quot;+(y2)+&quot; &quot;;
							svg +=(startloc+c*percolwidth+percolwidth+nlet-1 )+&quot;,&quot;+(y2-nlet)+&quot; &quot;
							svg +=(startloc+c*percolwidth+percolwidth+nlet-1 )+&quot;,&quot;+(h-nlet)+&quot;\&quot; /&gt;&quot;;
							//上面
							svg += &quot;&lt;polygon fill=\&quot;&quot;+this.m_color[c%this.m_color.length]+&quot;\&quot;  points=\&quot;&quot;;
							svg +=(startloc+c*percolwidth)+&quot;,&quot;+(h)+&quot; &quot;;
							svg +=(startloc+c*percolwidth+percolwidth-1)+&quot;,&quot;+(h)+&quot; &quot;;
							svg +=(startloc+c*percolwidth+percolwidth+nlet-1 )+&quot;,&quot;+(h-nlet)+&quot; &quot;
							svg +=(startloc+c*percolwidth+nlet )+&quot;,&quot;+(h-nlet)+&quot;\&quot; /&gt;&quot;;
						}
					}

				}
			}
		}

		// 显示右边的颜色说明
		if (this.m_bShowSeralName == 1 ) {		// 是否在右边显示系列名称
			for ( var c=0;c&lt;colcount;c++ ) {
				var colnam= _this.XMLDS_GetColumnName(c);
				svg += &quot;&lt;rect fill=\&quot;&quot;+this.m_color[c%this.m_color.length]+&quot;\&quot; x=\&quot;&quot;+(x2+10)+&quot;\&quot; y=\&quot;&quot;+(y1+c*20)+&quot;\&quot; width=\&quot;10\&quot; height=\&quot;10\&quot; /&gt;&quot;;
				svg += &quot;&lt;text x=\&quot;&quot;+(x2+25)+&quot;\&quot; y=\&quot;&quot;+(y1+c*20-2)+&quot;\&quot; width=\&quot;&quot;+(width-x2-25)+&quot;\&quot; height=\&quot;20\&quot; &gt;&quot;+colnam+&quot;&lt;/text&gt;&quot;;
			}
		}
		// 标题
		//svg += &quot;&lt;text x=\&quot;&quot;+x1+&quot;\&quot; y=\&quot;5\&quot; width=\&quot;&quot;+(x2-x1)+&quot;\&quot; height=\&quot;&quot;+(x1-20)+&quot;\&quot; font-family=\&quot;&quot;+this.m_titlefontname +&quot;\&quot; font-size=\&quot;&quot;+this.m_titlefontsize+&quot;\&quot; align=\&quot;center\&quot;&gt;&quot;+this.m_title+&quot;&lt;/text&gt;&quot;;
		svg += &quot;&lt;text x=\&quot;&quot;+x1+&quot;\&quot; y=\&quot;5\&quot; width=\&quot;&quot;+(x2-x1)+&quot;\&quot; height=\&quot;&quot;+y1+&quot;\&quot; font-family=\&quot;&quot;+this.m_titlefontname +&quot;\&quot; font-size=\&quot;&quot;+this.m_titlefontsize+&quot;\&quot; align=\&quot;center\&quot; valign=\&quot;middle\&quot;&gt;&quot;+this.m_title+&quot;&lt;/text&gt;&quot;;
		var ytitle= this.m_ytitle;
		if ( this.m_ymulti ==10000 ) ytitle += &quot;(&quot;+lang_main.wan+&quot;)&quot;;
		else if ( this.m_ymulti ==1000 ) ytitle += &quot;(&quot;+lang_main.thousand+&quot;)&quot;;
		else if ( this.m_ymulti ==100 ) ytitle += &quot;(&quot;+lang_main.hendred+&quot;)&quot;;
		else if ( this.m_ymulti ==1000000) ytitle += &quot;(&quot;+lang_main.baiwan+&quot;)&quot;;
		else if ( this.m_ymulti ==100000000) ytitle += &quot;(&quot;+lang_main.yi+&quot;)&quot;;
		svg += &quot;&lt;text x=\&quot;&quot;+(x1+5)+&quot;\&quot; y=\&quot;5\&quot; height=\&quot;&quot;+y1+&quot;\&quot; align=\&quot;left\&quot; valign=\&quot;middle\&quot; font-family=\&quot;&quot;+this.m_ytitlefontname +&quot;\&quot; font-size=\&quot;&quot;+this.m_ytitlefontsize+&quot;\&quot; &gt;&quot;+ytitle+&quot;&lt;/text&gt;&quot;;
		svg += &quot;&lt;text x=\&quot;&quot;+(x2-8)+&quot;\&quot; y=\&quot;&quot;+(y2+5)+&quot;\&quot; width=\&quot;&quot;+(width-x2)+&quot;\&quot; heigth=\&quot;&quot;+(height-y2)+&quot;\&quot; font-family=\&quot;&quot;+this.m_xtitlefontname +&quot;\&quot; font-size=\&quot;&quot;+this.m_xtitlefontsize+&quot;\&quot; &gt;&quot;+this.m_xtitle+&quot;&lt;/text&gt;&quot;;
	}
	svg+=&quot;&lt;/svg&gt;&quot;;
	_this.SetToSVGForm(this.m_objid ,this.m_sheet,this.m_row,this.m_col,svg);
}
