var sheet=0;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
//	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_MULTIROW_CHECKBOX );

	var list1 = _this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_TAX_YTAXMAN&quot;),&quot;ID&quot;,&quot;NAME&quot; );//创建下拉框
	var list2 = _this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_TAX_ELECOMPANY&quot;),&quot;ID&quot;,&quot;NAME&quot; );//创建下拉框

	var sql = &quot; select id,name from V_TAX_TOWNS &quot;;
	for(var row=_this.GetMainCellRangeRow1(sheet);row&lt;=_this.GetMainCellRangeRow2(sheet);row++)
	{
		_this.SetToComboBox(&quot;&quot;,0,row,10,createListValues(sql));
		_this.SetToComboBox(&quot;&quot;,0,row,8,list1);
		_this.SetToComboBox(&quot;&quot;,0,row,6,list2);
		_this.SetToBoolBox(sheet,row,0);
	}
	_this.SetColVisible(sheet,1,-1);
	_this.SetColVisible(sheet,13,-1);
}

//创建下拉框
function createListValues(sql){
	var id = &quot;&quot;;
	var name = &quot;&quot;;
	var listid = _this.CreateListValue();
	QuerySQLDS(sql);
	var count = _this.XMLDS_GetRowCount();
	for(var i = 0;i &lt; count;i ++){
		id = _this.XMLDS_GetString(i,&quot;ID&quot;);
		name = _this.XMLDS_GetString(i,&quot;NAME&quot;);
		_this.SetListValue(listid,id,name);
	}
	return listid ;
}
