 	alert(msg);
 	window.location.reload();	
}

//删除
function deleRow()
{
	var r = getSelectedRow();
	if (r == -1) {
		alert(&quot;没有选中记录！&quot;);
		return;
	}
	if (!confirm(&quot;是否确定删除该记录？&quot;)) return;
	var msg = invokeOSFunc(&quot;del&quot;,&quot;guid=&quot;+_this.GetCellText(0,r,1));
	if (msg &gt; 0) {
		alert(&quot;操作成功！&quot;);
		_this.DeleteRow(0,r);
	}
	else {
		alert(&quot;操作失败！&quot;+msg);
	}
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if(sheet == 0&&row &gt;= _this.GetMainCellRangeRow1(sheet)&&row &lt;= _this.GetMainCellRangeRow2(sheet)&&col == 0)
	{
		if(_this.GetCellText(sheet,row,col) == &quot;1&quot;)
		{
			for(var r=_this.GetMainCellRangeRow1(sheet);r&lt;=_this.GetMainCellRangeRow2(sheet);r++) 
			{
				if(r != row)
					_this.SetCellText(sheet,r,col,&quot;0&quot;);	
			}
		}
	}
} 


function getSelectedRow()
{
	for (var r=1;r&lt;_this.GetRowCount(0);r++) {
		if (_this.GetCellText(0,r,0) == &quot;1&quot;) {
			return r;
		}
	}
	return -1;
}

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	for (var r=1;r&lt;_this.GetRowCount(0);r++) {
		_this.SetCellText(sheet,r,0,&quot;0&quot;);
	}
	_this.SetCellText(sheet,row,0,&quot;1&quot;);

	var url = &quot;show.sp?grdid=CMEDIT&guid=&quot;+_this.GetCellText(sheet,row,1)+&quot;&act=update&quot;;
	var ret = window.showModalDialog(url,window,&quot;scroll:no;status:no;dialogWidth:500px;dialoHeight:100px&quot;);
	if (ret != undefined && ret != &quot;&quot;) {
		window.location.reload(true);
	}

}

//修改
function modify()
{
	var row = getSelectedRow();
	if (row != -1) {
		var url = &quot;show.sp?grdid=CMEDIT&guid=&quot;+_this.GetCellText(sheet,row,1)+&quot;&act=update&quot;;
		var ret = window.showModalDialog(url,window,&quot;scroll:no;status:no;dialogWidth:500px;dialoHeight:100px&quot;);
		if (ret != undefined && ret != &quot;&quot;) {
			window.location.reload(true);
		}
	}
}

ROW.GRDOSDS_VALUE:
var pubpack = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);
var xlsdb = new JavaPackage ( &quot;com.xlsgrid.net.xlsdb&quot; );

//更新
function save()
{
 	var db=null;
 	var ds= null;
 	var count1=0;
 	var sql = &quot;&quot;;
	try {   
		db = new pubpack.EADatabase();
		var ds = new pubpack.EAXmlDS(xmlstrs);	

		for(var row=0;row&lt;ds.getRowCount();row++){
			var id =ds.getStringAt(row,&quot;ID&quot;);
			var name= ds.getStringAt(row,&quot;name&quot;);
	 		var ammno=  ds.getStringAt(row,&quot;ammno&quot;);
			var typ= ds.getStringAt(row,&quot;typ&quot;);
			var pwdept= ds.getStringAt(row,&quot;idd&quot;);
			var hycode= ds.getStringAt(row,&quot;hycode&quot;);
			var ytaxman= ds.getStringAt(row,&quot;idc&quot;);
			var taxman= ds.getStringAt(row,&quot;taxman&quot;);
			var towns= ds.getStringAt(row,&quot;idb&quot;);
			var lawman= ds.getStringAt(row,&quot;lawman&quot;);
			var addr= ds.getStringAt(row,&quot;addr&quot;);

		sql = &quot;update tax_company a set (name,hycode,pwdept,typ,taxman,towns,lawman,addr,ytaxman)=
			(select &apos;&quot;+name+&quot;&apos;,&apos;&quot;+hycode+&quot;&apos;,(select name from V_TAX_ELECOMPANY WHERE ID=&apos;&quot;+pwdept+&quot;&apos;),&apos;&quot;+typ+&quot;&apos;,&apos;&quot;+taxman+&quot;&apos;,(select name from V_TAX_TOWNS WHERE ID=&apos;&quot;+towns+&quot;&apos;),&apos;&quot;+lawman+&quot;&apos;,&apos;&quot;+addr+&quot;&apos;,(select name from V_TAX_YTAXMAN WHERE ID=&apos;&quot;+ytaxman+&quot;&apos;)
			 from tax_company where tax_company.ID=&apos;&quot;+id+&quot;&apos;and tax_company.ammno=&apos;&quot;+ammno+&quot;&apos;)
			where id = (select distinct &apos;&quot;+id+&quot;&apos;from tax_company)&quot;;

			count1 = db.ExcecutSQL(sql);
	  	}
	  	 db.Commit();
	 	 return &quot;更新&quot;+count1+&quot;条&quot;;
	}catch(e){
		if(db != null) db.Rollback();
		throw new Exception(e);
	}finally{

		db.Close();
	}
}

function del()
{
	try {
		var sql = &quot;delete from tax_company where guid=&apos;&quot;+guid+&quot;&apos;&quot;;
		var ret = pubpack.EADbTool.ExcecutSQL(sql);
		return ret;
	}
	catch(e) {
		return e.toString();
	}
}

//添加
function save1()
{
 	var db = null;
	var ds = null;
	var sql = &quot;&quot;;
	var count = 0;
	try {   
		db = new pubpack.EADatabase();

		sql = &quot;insert into tax_company(ID,NAME,AMMNO,HYCODE,PWDEPT,TYP,TAXMAN,TOWNS,LAWMAN,ADDR,YTAXMAN,typclsid,stat) 
			select ID,NAME,&apos;&quot;+addammno+&quot;&apos;,HYCODE,PWDEPT,TYP,TAXMAN,TOWNS,LAWMAN,ADDR,YTAXMAN,typclsid,stat 
			from tax_company where id=&apos;&quot;+cmid+&quot;&apos; and rownum=1&quot;;

		count = db.ExcecutSQL(sql);  
	 	db.Commit();
		return &quot;添加电表号成功，表电号：&quot;+addammno;
	}catch(e){
		if(db != null) db.Rollback();
		throw new Exception(e);
	}finally{
		db.Close();
	}
}

ROW.DATDSC:
select * from (select &apos;&apos; FLAG,a.guid,a.id,a.name,a.ammno,a.typ,d.id idd,a.hycode,c.id idc,a.taxman,
b.id idb,a.lawman,a.addr,a.typclsid
from tax_company a,V_TAX_TOWNS b,V_TAX_YTAXMAN c,V_TAX_ELECOMPANY d
where nvl(a.taxman,&apos; &apos;) like &apos;[%TAXMAN]%&apos;
  and nvl(a.ammno,&apos; &apos;) like &apos;[%AMM]%&apos;
  and decode(a.ytaxman,&apos;1&apos;,&apos;是&apos;,&apos;否&apos;)=c.name(+)
  and a.pwdept=d.name(+)
  and a.towns=b.name(+)
  AND nvl(a.HYCODE,&apos; &apos;) LIKE &apos;[%HYDM]%&apos;
  AND nvl(a.TYP,&apos; &apos;) LIKE &apos;[%CMTYP]%&apos;
  and a.id like &apos;[%CMID]%&apos;
  and a.towns IN (select name from V_TAX_TOWNS WHERE ID like &apos;[%TOWN]%&apos;)
) a where idc like &apos;[%NSR]%&apos; order by id,ammno
