ROW.GRDJSDS_VALUE:
var sheet=0;
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	for(var row=6;row&lt;=_this.GetMainCellRangeRow2(sheet);row++)
	{
		_this.SetToBoolBox(sheet,row,1);
	}

	var chkrol = invokeOSFunc(&quot;TAXMON.DesktopLayout.checkGenTask&quot;,&quot;usrid=&quot;+G_USRID+&quot;&thisorgid=&quot;+G_ORGID);
	if (chkrol &gt; 0) {
		_this.AddToolbarButton(&quot;udf_genTasks&quot;,&quot;生成核实任务&quot;,&quot;&quot;,&quot;&quot;,0,0,0,100);
	}
}

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	if ( row&gt;=6 && row&gt;=_this.GetMainCellRangeRow1(sheet)&& row&lt;=_this.GetMainCellRangeRow2(sheet) ) {
		var id= _this.GetCellText(sheet,row,2);
		window.open(&quot;show.sp?grdid=EleNoTax&YYMM1=&quot;+YYMM1+&quot;&YYMM2=&quot;+YYMM2+&quot;&id=&quot;+id);
	}
}



//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (row == 6 && col == 1) {
		for(var r=6;r&lt;=_this.GetMainCellRangeRow2(sheet);r++) {
			_this.SetCellText(sheet,r,1,newvalue);
		}

	}  
}


//生成
function genTasks()
{
	var xmlds = getXmlStr(sheet,7,1,_this.GetMainCellRangeRow2(sheet),_this.GetMainCellRangeCol2(sheet));

	var usrids=G_USRID; //用户编号
	var accids=G_ACCID; //帐套号
	var orgs=G_ORGID; //组织号
	var syts = G_SYTID; //系统号 
	var usrnams=G_USRNAM ; //用户名称
	var typ=&quot;1&quot;;

	var param = new Object();	
	param.usrids=usrids;
	param.accids=accids;
	param.orgs=orgs;
	param.syts=syts;
	param.typ=typ;
	param.yymm1 = YYMM1;
	param.yymm2 = YYMM2;
	param.xmlstr = xmlds;
	param.usrnams=usrnams;
	var enddat = window.showModalDialog(&quot;show.sp?grdid=TKParam&typ=&quot;+typ,&quot;&quot;,&quot;dialogHeight=300px;dialogWidth=420px;toolbar=no;menubar=no;scrollbars=no;resizable=no;location=no;status=no&quot;);
	if (enddat != &quot;undefined&quot; && enddat != null) {
		param.enddat = enddat;
		var msg = invokeOSFuncExt(&quot;TAXMON.DesktopLayout.insertTasks&quot;,param);
		alert(msg);
	}


}


function getXmlStr(sheet,row1,col1,row2,col2)
{
	var xmlstr = &quot;&lt;?xml version=&apos;1.0&apos;?&gt;&lt;ROWSET&gt;&quot;;
	var i = 0;
	for (var r=row1;r&lt;=row2;r++) {
		if (_this.GetCellText(sheet,r,col1) == &quot;1&quot;) {
			xmlstr += &quot;&lt;ROW num=\&quot;&quot;+(++i)+&quot;\&quot;&gt;\n&quot;;
			for (var c=col1;c&lt;=col2;c++) {
				var cname = _this.GetColName(sheet,c);
				var str = _this.GetCellText(sheet,r,c);
				xmlstr += &quot;&lt;&quot;+cname+&quot;&gt;&quot;+str+&quot;&lt;/&quot;+cname+&quot;&gt;\n&quot;
			}
			xmlstr += &quot;&lt;/ROW&gt;\n&quot;;
		}
	}
	xmlstr += &quot;&lt;/ROWSET&gt;&quot;;
	return xmlstr;
}
