ROW.GRDJSDS_VALUE:
var sheet =0;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	for(var i=5;i&lt;=_this.GetMainCellRangeRow2(sheet);i++)
	{
           _this.SetToBoolBox(sheet,i,1);
	 }
}

//生成
function guid()
{
 	 for(var row=5;row&lt;_this.GetMainCellRangeRow2(sheet);row++){
		if(_this.GetCellText(sheet,row,1)==1){
			var id=_this.GetCellText(sheet,row,2);
			var name=_this.GetCellText(sheet,row,3);
			var taxmony=_this.GetCellText(sheet,row,4);
			var taxmny=_this.GetCellText(sheet,row,5);
			var taxman=_this.GetCellText(sheet,row,6);
			var star=_this.GetCellText(sheet,row,7);
			var usrid=G_USRID;
			var accid=G_ACCID; //帐套号
			var org=G_ORGID; //组织号
			var syt = G_SYTID; //系统号

			var param = new Object();
			param.id=id;
			param.name=name;
			param.taxmony=taxmony;
			param.taxmny=taxmny;
			param.taxman=taxman;
			param.star=star;
			param.usrid=usrid;
			param.accid=accid;
			param.org=org;
			param.syt=syt;

			var msg = invokeOSFuncExt(&quot;insert&quot;,param);
			alert(msg);
		}
	}

}

ROW.GRDOSDS_VALUE:
var pubpack = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);
var xlsdb = new JavaPackage(&quot;com.xlsgrid.net.xlsdb&quot;);


function insert()
{
	var db = null;
	var ds = null;
	var sql = &quot;&quot;;
	var id = id;
	var name = name;
	var taxmony = taxmony;
	var taxmny = taxmny;
	var taxman = taxman;
	var star =star;
	var usrid = usrid;
	var accid = accid;
	var org=org;
	var syt=syt;

	try{
		db = new pubpack.EADatabase();
		var chkusr =db.GetBillid(accid,&quot;&quot;,&quot;1&quot;);    //随机产生审批人编号
		var sysdat=db.GetSQL(&quot;select to_char(sysdate,&apos;yyyy-mm-dd hh24:mi:ss&apos;) from dual&quot;);

		sql = &quot;insert into tax_trkhdr(ACC,ORG,SYT,CHKUSR,CHKUSRNAM,CHKDAT,Cmid,Cmnam,Tousr,SUBTYP) 
		select &apos;&quot;+accid+&quot;&apos;,&apos;&quot;+org+&quot;&apos;,&apos;&quot;+syt+&quot;&apos;,&apos;&quot;+chkusr+&quot;&apos;,&apos;&quot;+usrid+&quot;&apos;,to_date(&apos;&quot;+sysdat+&quot;&apos;,&apos;yyyy-MM-dd hh24:mi:ss&apos;),&apos;&quot;+id+&quot;&apos; ,&apos;&quot;+name+&quot;&apos;,&apos;&quot;+taxman+&quot;&apos;,&apos;2&apos;  
		from dual where not exists (select 1 from tax_trkhdr a where a.Cmid = &apos;&quot;+id+&quot;&apos;)&quot;;

		var count = db.ExcecutSQL(sql);

		if(count&gt;0)
			return &quot;生成成功&quot;;
		else
			return &quot;以生成&quot;;
	}catch(e){
		if(db != null) db.Rollback();
			return e.toString();

	}finally{
		if(db != null)	db.Close();
	}

}
