			note = pubpack.EAFunc.Replace(note,&quot;\u0009&quot;,&quot;&nbsp;&nbsp;&nbsp;&nbsp;&quot;);//替换tab键

			/*
			if (i &gt; 0) html += &quot;&lt;br&gt;&lt;hr&gt;&quot;;
			html += &quot;&lt;p align=left&gt;&lt;font size=2&gt;【&quot;+yymm+&quot;】&nbsp;&nbsp;&quot;+title+&quot;&lt;/font&gt;&nbsp;&nbsp;&quot;;
