var sheet=0;
//数据加载完毕
var as=0;
function _thisOnpost_loaddata(sheet)
{
	for(var row=2;row&lt;=_this.GetMainCellRangeRow2(sheet);row++)
	{
		_this.SetToBoolBox(sheet,row,1);
	}
	if(as==0){
	var Addmenu = &quot;&lt;item id=\&quot;isNoTax\&quot; name=\&quot;非国税管理企业\&quot;/&gt;&lt;item id=\&quot;isTax\&quot; name=\&quot;国税管理企业\&quot;/&gt;&quot;;
	_this.AddToolbarButton(&quot;&quot;,&quot;标志为&quot;,&quot;&quot;,Addmenu,0,0,0,70);
	as = 1;
	}
	var DBNO= _this.GetCellText(0,2,3);
        _sql.querytods(&quot;isTax&quot;,&quot;DBNO=&quot;+DBNO);
	var isT = _this.XMLDS_GetString(0,&quot;no&quot;);
	if(isT==&apos;0&apos;){
		_this.SetCellText(0,2,9,&quot;是&quot;);
	}else{_this.SetCellText(0,2,9,&quot;否&quot;);
}
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
  if(row==5 && col==1)
  {
  	for(var r=5;r&lt;=_this.GetMainCellRangeRow2(sheet); r++){
  		_this.SetCellText(sheet,r,1,newvalue);
  	}
  }
}


function getSelectRow()
{
	var comps = &quot;&quot;;
	for (var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++) {
		if (_this.GetCellText(0,r,1) == &quot;1&quot;) {
			var id = _this.GetCellText(0,r,3);
			if (comps == &quot;&quot;) comps += id;
			else comps += &quot;,&quot; + id;
		}
	}
	return comps;
}
//非国税管理企业
function isnoTaxCompany()
{

	var comps = getSelectRow();

	if (comps != &quot;&quot;) {
		if (!confirm(&quot;是否标志为非国税管理企业？&quot;)) return;
		var param = new Object();
		param.cms = comps;
		param.thisusrid = G_USRID;
		var ret = invokeOSFuncExt(&quot;updateFlag&quot;,param);
		alert(ret);
	}else{ alert(&quot;请选中一条记录&quot;);}


}

//国税管理企业
function isTaxCompany()
{

	var comps = getSelectRow();

	if (comps != &quot;&quot;) {
		if (!confirm(&quot;是否标志为国税管理企业？&quot;)) return;
		var param = new Object();
		param.cms = comps;
		param.thisusrid = G_USRID;
		var ret = invokeOSFuncExt(&quot;updateFlag2&quot;,param);
		alert(ret);
	}else{ alert(&quot;请选中一条记录&quot;);}
}

//点击菜单项
function _thisOnMenuItemSelect(sheet,row,col,menuitemid)
{
	if (menuitemid == &quot;isNoTax&quot;) {
		isnoTaxCompany();
	}
	else if (menuitemid == &quot;isTax&quot;) {
		isTaxCompany();
	}
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);
function updateFlag()
{
	var db = null;
	var sql = &quot;&quot;;
	var ret = 0;
	try {
		db = new pub.EADatabase();
		var ammnos = cms.split(&quot;,&quot;);
		for (var i=0;i&lt;ammnos.length();i++) {
			sql = &quot;insert into tax_notinammno(ammno,crtusr) values(&apos;&quot;+ammnos[i]+&quot;&apos;,&apos;&quot;+thisusrid+&quot;&apos;)&quot;;
			ret += db.ExcecutSQL(sql);
		}

		db.Commit();
		return &quot;操作成功! 标志记录数&quot;+ret;
	}
	catch(e) {
		if (db != null) db.Rollback();
		return &quot;操作失败&quot;+e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}

function updateFlag2()
{
	var db = null;
	var sql = &quot;&quot;;
	var ret = 0;
	try {
		db = new pub.EADatabase();
		var ammnos = cms.split(&quot;,&quot;);
		for (var i=0;i&lt;ammnos.length();i++) {
			sql = &quot;delete from tax_notinammno where ammno=&apos;&quot;+ammnos[i]+&quot;&apos;&quot;;
			ret += db.ExcecutSQL(sql);
		}

		db.Commit();
		return &quot;操作成功! 标志记录数&quot;+ret;
	}
	catch(e) {
		if (db != null) db.Rollback();
		return &quot;操作失败&quot;+e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}

ROW.DATDSC:
select flag,rownum rn,id,name,gds,eleqty,elepro,addr from (
select  &apos;&apos; flag, id,name,gds,eleqty,elepro,addr from (
select usrid id,usrnam name,sum(nvl(eleqty,0)) eleqty,max(gds) gds,max(elepro) elepro,max(a.addr) addr
from tax_eledata a,(
  select b.ID,b.name,b.flag,c.ammno from tax_company b,tax_cmpammno c
  where b.id=c.cmid ) b
where a.usrid=b.ammno(+) 
and a.yymm&gt;=decode(&apos;[%YYMM1]&apos;,&apos;&apos;,&apos;1900-01&apos;,&apos;[%YYMM1]&apos;) 
and a.yymm&lt;=decode(&apos;[%YYMM2]&apos;,&apos;&apos;,&apos;2900-01&apos;,&apos;[%YYMM2]&apos;)
and b.ammno = &apos;[%DBNO]&apos;
group by usrid,usrnam
) 
)
