ROW.GRDJSDS_VALUE:
var sheet=0;
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	for(var row=5;row&lt;=_this.GetMainCellRangeRow2(sheet);row++)
	{
		_this.SetToBoolBox(sheet,row,1);
	}

	var chkrol = invokeOSFunc(&quot;GXHZTAX.DesktopLayout.checkGenTask&quot;,&quot;usrid=&quot;+G_USRID+&quot;&thisorgid=&quot;+G_ORGID);
	if (chkrol &gt; 0) {
		_this.AddToolbarButton(&quot;udf_genTasks&quot;,&quot;生成核实任务&quot;,&quot;&quot;,&quot;&quot;,0,0,0,100);
	}

	var Addmenu = &quot;&lt;item id=\&quot;isNoTax\&quot; name=\&quot;非国税管理企业\&quot;/&gt;&lt;item id=\&quot;isTax\&quot; name=\&quot;国税管理企业\&quot;/&gt;&quot;;
	_this.AddToolbarButton(&quot;&quot;,&quot;标志为&quot;,&quot;&quot;,Addmenu,0,0,0,70);	

}


//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
  if(row==5 && col==1)
  {
  	for(var r=5;r&lt;=_this.GetMainCellRangeRow2(sheet); r++){
  		_this.SetCellText(sheet,r,1,newvalue);
  	}
  }
}


function getXmlStr(sheet,row1,col1,row2,col2)
{
	var xmlstr = &quot;&lt;?xml version=&apos;1.0&apos;?&gt;&lt;ROWSET&gt;&quot;;
	var i = 0;
	for (var r=row1;r&lt;=row2;r++) {
		if (_this.GetCellText(sheet,r,col1) == &quot;1&quot;) {
			xmlstr += &quot;&lt;ROW num=\&quot;&quot;+(++i)+&quot;\&quot;&gt;\n&quot;;
			for (var c=col1;c&lt;=col2;c++) {
				var cname = _this.GetColName(sheet,c);
				var str = _this.GetCellText(sheet,r,c);
				xmlstr += &quot;&lt;&quot;+cname+&quot;&gt;&quot;+str+&quot;&lt;/&quot;+cname+&quot;&gt;\n&quot;
			}
			xmlstr += &quot;&lt;/ROW&gt;\n&quot;;
		}
	}
	xmlstr += &quot;&lt;/ROWSET&gt;&quot;;
	return xmlstr;
}

function getSelectRow()
{
	var comps = &quot;&quot;;
	for (var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++) {
		if (_this.GetCellText(0,r,1) == &quot;1&quot;) {
			var id = _this.GetCellText(0,r,3);
			if (comps == &quot;&quot;) comps += id;
			else comps += &quot;,&quot; + id;
		}
	}
	return comps;
}

//非国税管理企业
function isnoTaxCompany()
{
	if (!confirm(&quot;是否标志为非国税管理企业？&quot;)) return;
	var comps = getSelectRow();

	if (comps != &quot;&quot;) {
		var param = new Object();
		param.cms = comps;
		param.thisusrid = G_USRID;
		var ret = invokeOSFuncExt(&quot;updateFlag&quot;,param);
		alert(ret);
	}
}

//国税管理企业
function isTaxCompany()
{
	if (!confirm(&quot;是否标志为国税管理企业？&quot;)) return;
	var comps = getSelectRow();

	if (comps != &quot;&quot;) {
		var param = new Object();
		param.cms = comps;
		param.thisusrid = G_USRID;
		var ret = invokeOSFuncExt(&quot;updateFlag2&quot;,param);
		alert(ret);
	}
}

//点击菜单项
function _thisOnMenuItemSelect(sheet,row,col,menuitemid)
{
	if (menuitemid == &quot;isNoTax&quot;) {
		isnoTaxCompany();
	}
	else if (menuitemid == &quot;isTax&quot;) {
		isTaxCompany();
	}
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

//替换SQL参数
function replaceParam(mwobj,request,sql)
{
	var town = pub.EAFunc.NVL(request.getParameter(&quot;TOWNS&quot;),&quot;&quot;);
	var flg = pub.EAFunc.NVL(request.getParameter(&quot;FLG&quot;),&quot;&quot;);
	if (town != &quot;&quot;) {
		var townids = pub.EAFunc.SQLIN(town);		
		town = &quot; and town in (select name from v_tax_elearea where id in (&quot;+townids+&quot;))&quot;;
	}
	sql = pub.EAFunc.Replace(sql,&quot;[%TOWNS]&quot;,town);

	if (flg == &quot;1&quot;) {
		sql = pub.EAFunc.Replace(sql,&quot;[%FLGSTR]&quot;,&quot; and a.usrid not in (select ammno from tax_notinammno)&quot;);
	}
	if (flg == &quot;0&quot;) {
		sql = pub.EAFunc.Replace(sql,&quot;[%FLGSTR]&quot;,&quot; and a.usrid in (select ammno from tax_notinammno)&quot;);
	}
	else {
		sql = pub.EAFunc.Replace(sql,&quot;[%FLGSTR]&quot;,&quot;&quot;);
	}

	var gds = request.getParameter(&quot;GDS&quot;);
	if (gds != &quot;&quot;) {
		sql = pub.EAFunc.Replace(sql,&quot;[%GDSSTR]&quot;,&quot;and nvl(a.gds,&apos;&apos;) IN (select name from v_tax_gds where ID like &apos;&quot;+gds+&quot;%&apos;)&quot;);
	}
	else {
		sql = pub.EAFunc.Replace(sql,&quot;[%GDSSTR]&quot;,&quot;&quot;);
	}
	return sql;

}


function updateFlag()
{
	var db = null;
	var sql = &quot;&quot;;
	var ret = 0;
	try {
		db = new pub.EADatabase();
		var ammnos = cms.split(&quot;,&quot;);
		for (var i=0;i&lt;ammnos.length();i++) {
			sql = &quot;insert into tax_notinammno(ammno,crtusr) values(&apos;&quot;+ammnos[i]+&quot;&apos;,&apos;&quot;+thisusrid+&quot;&apos;)&quot;;
			ret += db.ExcecutSQL(sql);
		}

		db.Commit();
		return &quot;操作成功! 标志记录数&quot;+ret;
	}
	catch(e) {
		if (db != null) db.Rollback();
		return &quot;操作失败&quot;+e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}

function updateFlag2()
{
	var db = null;
	var sql = &quot;&quot;;
	var ret = 0;
	try {
		db = new pub.EADatabase();
		var ammnos = cms.split(&quot;,&quot;);
		for (var i=0;i&lt;ammnos.length();i++) {
			sql = &quot;delete from tax_notinammno where ammno=&apos;&quot;+ammnos[i]+&quot;&apos;&quot;;
			ret += db.ExcecutSQL(sql);
		}

		db.Commit();
		return &quot;操作成功! 标志记录数&quot;+ret;
	}
	catch(e) {
		if (db != null) db.Rollback();
		return &quot;操作失败&quot;+e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}
