var sheet =0;
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SetCellLock(sheet,4,6,1);
	_this.SetCellLock(sheet,5,6,0);
  	_this.SetCellLock(sheet,7,2,0);
  	_this.SetCellLock(sheet,10,2,0);
//  	_this.SetToFileSelectBox(&quot;&quot;,sheet,10,2);
  	if (_this.GetCellText(sheet,10,2) != &quot;&quot;) {
  		_this.SetCellText(sheet,11,6,&quot;[双击删除附件]&quot;);
  	}

	var tmp = _this.GetCellText(0,2,6);
	if(tmp == &quot;6&quot;)
	{
		_this.SetCellText(0,4,1,&quot;电表编号：&quot;);
		var where = &quot; id =&apos;2&apos; or id=&apos;3&apos;&quot;;
		var typlist =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_TAX_TRKNEWSUBTYP&where=&quot;+where),&quot;ID&quot;,&quot;NAME&quot;);
		_this.SetToComboBox(&quot;&quot;,sheet,5,6,typlist);
	}
	if(tmp == &quot;5&quot;)
	{
		var where = &quot; id =&apos;2&apos; or id=&apos;4&apos;&quot;;
		var typlist =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_TAX_TRKNEWSUBTYP&where=&quot;+where),&quot;ID&quot;,&quot;NAME&quot;);
		_this.SetToComboBox(&quot;&quot;,sheet,5,6,typlist);
	}
}
