function saves()
{alert(&quot;&quot;);
//	var bilid=_this.GetCellText(sheet,3,6);
//	_sql.querytods(&quot;getGuid&quot;,&quot;bilid=&quot;+bilid);
//	if ( _this.XMLDS_GetRowCount() &gt; 0 ) 
	var hdrguid = hdrguid;//_this.XMLDS_GetString(0,&quot;GUID&quot;);
	var file = _this.GetCellText(sheet,10,2);
	if( file != &quot;&quot; || file.length != 0 ) 
        {
              _this.OpenHttpRequest(&quot;EAFormBlobUpload.sp&quot;,&quot;UPLOAD&quot;);
              _this.AddHttpRequestParam(&quot;formguid&quot;,hdrguid,0);   
              _this.AddHttpRequestParam(&quot;sytid&quot;,G_SYTID,0);
              _this.AddHttpRequestParam(&quot;grdid&quot;,G_GRDID,0);
	      _this.AddHttpRequestParam(&quot;fileurl&quot;,file,0);
	      _this.AddHttpRequestParam(&quot;data&quot;,file,1);
	      window.status = &quot;正在上传附件...&quot;;
              var xml = _this.SendHttpRequest();                //开始上传   
              _this.CloseHttpRequest();  

              if( xml == &quot;&quot; )  
              { 
                    alert(&quot;操作错误,可能是网络连接错误.&quot;); 
              } 
              else 
              {
                     xml = &quot;上传附件成功&quot;;
                     alert (xml);
              }
       }
}


