//	if (subtyp == &quot;1&quot; || subtyp == &quot;2&quot;) {
//		if (!confirm(&quot;是否填写查补税款单？&quot;)) return;
//		//window.open(&quot;show.sp?grdid=PS&guid=&quot;);
//		//window.location.href = &quot;show.sp?grdid=PS&guid=&quot;;
//		//window.open(&quot;show.sp?grdid=PS&guid=&quot;,&quot;&quot;,&quot;dialogHeight=800px;dialogWidth=1000px;toolbar=no;menubar=no;scrollbars=no;resizable=no;location=no;status=no&quot;);
//		//window.location.href = &quot;show.sp?grdid=PS&guid=&action=entry&quot;;
//		window.open(&quot;show.sp?grdid=PS&guid=&action=entry&quot;);
//	}

	var subtyp = _this.GetCellText(0,5,6);
	if(subtyp==&quot;1&quot;){
		alert(&quot;您选择的是企业补报流程，接下来请填写查补台账!&quot;);
	}
	if(subtyp==&quot;2&quot;){
		alert(&quot;您选择的是电表维护流程，接下来请进行企业电表操作!&quot;);
	}
	if(subtyp==&quot;3&quot;){
		alert(&quot;您选择的是非国税管户流程，接下来请提交电表号!&quot;);
	}
	if(subtyp==&quot;4&quot;){
		alert(&quot;您选择的是非工业纳税户流程，接下来请提交纳税人识别号!&quot;);
	}
	if(subtyp==&quot;5&quot;){
		alert(&quot;您选择的是文字说明处理流程，接下来请填写文字说明!&quot;);
	}
	if(subtyp==&quot;6&quot;){
		alert(&quot;您选择的是漏管户处理流程，接下来请填写文字说明!&quot;);
	}
}

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	if (row = 11 && col == 6 && _this.GetCellText(sheet,row,col) != &quot;&quot;) {
		if (!confirm(&quot;删除附件，是否确定？&quot;)) return;
		var ret = _sql.run(&quot;DELFILE&quot;,&quot;GUID=&quot;+guid);
		if (ret &gt; 0) {
			alert(&quot;删除附件成功！&quot;);
			_this.SetCellText(sheet,10,2,&quot;&quot;);
			_thisOnRunCellFocusChange(sheet,-1,-1,0,0);
//			window.location.href = window.location.href;
			window.name = &quot;__self&quot;;
			window.open(window.location.href, &quot;__self&quot;); 
		}
	}
}

