var tmp =&quot;&quot;;
function _thisOnpost_loaddata(sheet)
{
	_this.SetCellText(sheet,11,4,G_USRNAM);
	_this.SetCellText(sheet,6,2,0);

	tmp = _this.GetCellText(0,5,6);
	if(tmp == &quot;6&quot;)
	{
		_this.SetCellText(0,3,1,&quot;电表编号：&quot;);
	}
}

