//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	//alert(id);return;
	if (id == &quot;udf_stm2332&quot; ) {
		var res = _this.GetCellText(0,6,2);

		var param = new Object();
		param.ammno = _this.GetCellText(0,3,2);
		param.thisusrid = G_USRID;
		if(tmp == &quot;6&quot;){
			if(res == 0){
			var ret = invokeOSFuncExt(&quot;updateFlag&quot;,param);
			alert(ret);
			}else{
				var ret = invokeOSFuncExt(&quot;updateFlag2&quot;,param);
				alert(ret);
			}
		}
	}

	if (id == &quot;udf_stm2433&quot; ) {
		var res = _this.GetCellText(0,6,2);

		var param = new Object();
		param.ammno = _this.GetCellText(0,3,2);
		param.thisusrid = G_USRID;
		if(tmp != &quot;6&quot;){
			if(res == 0){
			var ret = invokeOSFuncExt(&quot;updateFlag3&quot;,param);
			alert(ret);
			}else{
				var ret = invokeOSFuncExt(&quot;updateFlag4&quot;,param);
			alert(ret);
			}
		}
	}
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);
function updateFlag()
{
	var db = null;
	var sql = &quot;&quot;;
	var ret = 0;
	try {
		db = new pub.EADatabase();
		sql = &quot;insert into tax_notinammno(ammno,crtusr) values(&apos;&quot;+ammno+&quot;&apos;,&apos;&quot;+thisusrid+&quot;&apos;)&quot;;
		ret += db.ExcecutSQL(sql);
		db.Commit();
		return &quot;任务完成! 已标志电表号为非国税管户&quot;+ret;
	}
	catch(e) {
		if (db != null) db.Rollback();
		return &quot;操作失败&quot;+e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}

function updateFlag2()
{
	var db = null;
	var sql = &quot;&quot;;
	var ret = 0;
	try {
		db = new pub.EADatabase();
		sql = &quot;delete from tax_notinammno where ammno=&apos;&quot;+ammno+&quot;&apos;&quot;;
		ret += db.ExcecutSQL(sql);

		db.Commit();
		return &quot;任务退回! 已标志电表号为国税管户&quot;+ret;
	}
	catch(e) {
		if (db != null) db.Rollback();
		return &quot;操作失败&quot;+e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}



