ROW.GRDJSDS_VALUE:
var typ = &quot;&quot;;
var trkguid = &quot;&quot;; //直接对一个任务进行分派

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if (id == &quot;确定&quot;) {
		var taxman = _this.GetCellText(0,3,2);
		var enddat = _this.GetCellText(0,5,2); 
		var ret = taxman + &quot;~&quot; + enddat;
		if (taxman == &quot;&quot;) {
			alert(&quot;请选择税管员！&quot;);
			return ;
		}
		if (trkguid != &quot;&quot;) {
			sendTrk();
			window.close();
		}
		else {
			window.returnValue = ret; 
			window.close();  
		}
	}
}

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	var where = &quot;deptid in (select deptid from usr where org=&apos;&quot;+G_ORGID+&quot;&apos; and id=&apos;&quot;+G_USRID+&quot;&apos;)&quot;;
	var taxman =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_TAX_TAXMAN&where=&quot;+where),&quot;ID&quot;,&quot;NAME&quot;);
	_this.SetToComboBox(&quot;&quot;,sheet,3,2,taxman);
	_this.SetToDateBox(&quot;&quot;,sheet,5,2,G_LOGDAT);

	try {
		typ = flag; //flag=1只列出本股室的税管员
	}
	catch(e){ }

	try { trkguid = GUID;} catch(e) {}
}


function sendTrk()
{
	var param = new Object();
	param.trkguid = trkguid;
	param.usrid = G_USRID;
	param.usrnam = G_USRNAM;
	param.tousr = _this.GetCellText(0,3,2);
	param.enddat = _this.GetCellText(0,5,2);
	try {
		var ret = invokeOSFuncExt(&quot;sendTrk&quot;,param);
		alert(ret);
	}
	catch (e) {
		alert(&quot;分派失败 &quot;+e);
	}

}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

function sendTrk()
{
	var db = null;
	try {
		db = new pub.EADatabase();
		var sql = &quot;&quot;;
		var ret = 0;
		var guid = trkguid;
		sql = &quot;update tax_trkhdr set stat=&apos;2&apos;,tousr=&apos;&quot;+tousr+&quot;&apos;,enddat=to_date(&apos;&quot;+enddat+&quot;&apos;,&apos;yyyy-mm-dd&apos;),chkusr=&apos;&quot;+usrid+&quot;&apos;,chkusrnam=&apos;&quot;+usrnam+&quot;&apos;,chkdat=sysdate where guid=&apos;&quot;+guid+&quot;&apos;&quot;;
		ret += db.ExcecutSQL(sql);
		db.Commit();

		return &quot;任务分派成功!&quot;;
	}
	catch(e) {
		if (db != null) db.Rollback();
		throw new Exception(e.toString());
	}	
	finally {
		if (db != null) db.Close();
	}
}
