ROW.GRDJSDS_VALUE:
var sheet =0;

function expExcel()
{

        var str = exphtml(0);
        document.write(str);
}
function exphtml(sheet)
{
      //_this.SetMainCellRange(0,2,1,5,3); 
     var row1 = 0;
     var row2 = _this.GetRowCount(sheet);
     var col1 = 0; 
     var col2 = _this.GetColCount(sheet); 
     var p =_this.GetAttribe(&quot;CELL_0_3_1&quot;,_this.ATTR_CELLMERGERCOL);  
     var pp =  _this.GetAttribe(&quot;CELL_0_3_2&quot;, _this.ATTR_PARENTMERGER_CELLROW) 
     var str = &quot;&quot;; 
     var xml = _this.GetStyleXml(0);   
     _this.XMLDS_Parse(xml);
     var length = _this.XMLDS_GetRowCount();
     var styleid = &quot;&quot;;
     var align = &quot;&quot;;
     var valign = &quot;&quot;;
     var multline = &quot;&quot;;
     var topborder = &quot;&quot;;
     var bottomborder = &quot;&quot;;
     var leftborder = &quot;&quot;;
     var rightborder = &quot;&quot;;
     var topbordercolor = &quot;&quot;;
     var bottombordercolor = &quot;&quot;;
     var leftbordercolor = &quot;&quot;;
     var rightbordercolor = &quot;&quot;;
     var fontname = &quot;&quot;;
     var fontsize = &quot;&quot;;
     var fontbold = &quot;&quot;;
     var fontitalic = &quot;&quot;;
     var fontstrikeout = &quot;&quot;;
     var fontunderline = &quot;&quot;;
     var fontcolor = &quot;&quot;;
     var backcolor = &quot;&quot;;
     var editable = &quot;&quot;;
     var printable = &quot;&quot;;
     var format = &quot;&quot;;
     var datetype = &quot;&quot;;  

     str += &quot;&lt;html&gt;&lt;head&gt;&lt;meta name=ProgId content=xlsGrid.net&gt;&lt;/meta&gt;&quot;;
     str += &quot;&lt;style&gt; \n&quot;+
            &quot;&lt;!--table\n&quot;+
	    &quot;{mso-displayed-decimal-separator:\n&quot;+&quot;\.\n&quot;+&quot;;{ \n&quot;+
	    &quot;mso-displayed-thousand-separator:\n&quot;+&quot;\,\n&quot;+&quot;;} \n&quot;+
            &quot;@page\n&quot;+
	    &quot;{margin:1.0in .75in 1.0in .75in;\n&quot;+
	    &quot;mso-header-margin:.5in;\n&quot;+
	    &quot;mso-footer-margin:.5in;}\n&quot;+
            &quot;tr\n&quot;+
	    &quot;{mso-height-source:auto;\n&quot;+
	    &quot;mso-ruby-visibility:none;}\n&quot;+
            &quot;col\n&quot;+
	    &quot;{mso-width-source:auto;\n&quot;+
	    &quot;mso-ruby-visibility:none;}\n&quot;+
            &quot;br\n&quot;+
	    &quot;{mso-data-placement:same-cell;}\n&quot;+
            &quot;.style0\n&quot;+
	   &quot;{mso-number-format:General;\n&quot;+
	   &quot;text-align:general;\n&quot;+
	&quot;vertical-align:middle;\n&quot;+
	&quot;white-space:nowrap;\n&quot;+
	&quot;mso-rotate:0;\n&quot;+
	&quot;mso-background-source:auto;\n&quot;+
	&quot;mso-pattern:auto;\n&quot;+
	&quot;color:windowtext;\n&quot;+
	&quot;font-size:9.0pt;\n&quot;+
	&quot;font-weight:400;\n&quot;+
	&quot;font-style:normal;\n&quot;+
	&quot;text-decoration:none;\n&quot;+
	&quot;font-family:SimSun;\n&quot;+
	&quot;mso-generic-font-family:auto;\n&quot;+
	&quot;mso-font-charset:134;\n&quot;+
	&quot;border:none;\n&quot;+
	&quot;mso-protection:locked visible;\n&quot;+
	&quot;mso-style-name:\5E38\89C4;\n&quot;+
	&quot;mso-style-id:0;}\n&quot;+
        &quot;td\n&quot;+
	&quot;{mso-style-parent:style0;\n&quot;+
	&quot;padding-top:1px;\n&quot;+
	&quot;padding-right:1px;\n&quot;+
	&quot;padding-left:1px;\n&quot;+
	&quot;mso-ignore:padding;\n&quot;+
	&quot;color:windowtext;\n&quot;+
	&quot;font-size:9.0pt;\n&quot;+
	&quot;font-weight:400;\n&quot;+
	&quot;font-style:normal;\n&quot;+
	&quot;text-decoration:none;\n&quot;+
	&quot;font-family:Arial;\n&quot;+
	&quot;mso-generic-font-family:auto;\n&quot;+
	&quot;mso-font-charset:134;\n&quot;+
	&quot;mso-number-format:General;\n&quot;+
	&quot;text-align:general;\n&quot;+
	&quot;vertical-align:middle;\n&quot;+
	&quot;border:none;\n&quot;+
	&quot;mso-background-source:auto;\n&quot;+
	&quot;mso-pattern:auto;\n&quot;+
	&quot;mso-protection:locked visible;\n&quot;+
	&quot;white-space:nowrap;\n&quot;+
	&quot;mso-rotate:0;}\n&quot;+
	&quot;display:none;}&quot;;
	for ( var i=0;i&lt;length;i++)
        {
           styleid = _this.XMLDS_GetString(i,&quot;ID&quot;);
           align =  _this.XMLDS_GetString(i,&quot;ALIGN&quot;);
           valign =  _this.XMLDS_GetString(i,&quot;VALIGN&quot;);
           multline =  _this.XMLDS_GetString(i,&quot;MULTLINE&quot;);
           topborder =  _this.XMLDS_GetString(i,&quot;TOPBORDER&quot;);
           bottomborder =  _this.XMLDS_GetString(i,&quot;BOTTOMBORDER&quot;);
           leftborder = _this.XMLDS_GetString(i,&quot;LEFTBORDER&quot;);
           rightborder =  _this.XMLDS_GetString(i,&quot;RIGHTBORDER&quot;);
           topbordercolor =  _this.XMLDS_GetString(i,&quot;TOPBORDERCOLOR&quot;);
           bottombordercolor =  _this.XMLDS_GetString(i,&quot;BOTTOMBORDERCOLOR&quot;);
           leftbordercolor =  _this.XMLDS_GetString(i,&quot;LEFTBORDERCOLOR&quot;);
           rightbordercolor =  _this.XMLDS_GetString(i,&quot;RIGHTBORDERCOLOR&quot;);
           fontname =  _this.XMLDS_GetString(i,&quot;FONTNAME&quot;);
           fontsize =  _this.XMLDS_GetString(i,&quot;FONTSIZE&quot;);
           fontbold =  _this.XMLDS_GetString(i,&quot;FONTBOLD&quot;);
           fontitalic =  _this.XMLDS_GetString(i,&quot;FONTITALIC&quot;);
           fontstrikeout =  _this.XMLDS_GetString(i,&quot;FONTTSTRICKEOUT&quot;);
           fontunderline =  _this.XMLDS_GetString(i,&quot;FONTUNDERLINE&quot;);
           fontcolor =  _this.XMLDS_GetString(i,&quot;FONTCOLOR&quot;);
           backcolor =  _this.XMLDS_GetString(i,&quot;BACKCOLOR&quot;);
           editable =  _this.XMLDS_GetString(i,&quot;EDITABLE&quot;);
           printable =  _this.XMLDS_GetString(i,&quot;PRINTABLE&quot;);
           format =  _this.XMLDS_GetString(i,&quot;FORMAT&quot;);
           datatype =  _this.XMLDS_GetString(i,&quot;DATATYPE&quot;);       
          // alert (fontitalic);
           str += &quot;.&quot;+styleid+&quot;\n&quot;;
           str += &quot;{\n&quot;;
           if(fontcolor!=&quot;#000000&quot;)str += &quot;color:&quot;+fontcolor+&quot;;\n&quot;;
           if(backcolor !=&quot;#FFFFFF&quot;)str += &quot;background:&quot;+backcolor +&quot;;\n&quot;;
           if(fontsize!=&quot;9&quot;) str +=&quot;font-size:&quot;+fontsize+&quot;pt;\n&quot;;
           if(fontbold!=&quot;1&quot;)str+=&quot;font-weight:&quot;+(fontbold==&quot;1&quot;?700:400)+&quot;;\n&quot;;
           // 自己处理        	&quot;font-style:&quot;+&quot;normal&quot;+&quot;;\n&quot;;
           if(fontitalic!=&quot;0&quot;) str+=&quot;font-style:&quot;+&quot;italic&quot;,+&quot;;\n&quot;;
           if(fontname!=&quot;Arial&quot;)str+=&quot;font-family:&quot;+fontname+&quot;;\n&quot;;
           str += &quot;text-align:&quot;+align+&quot;;\n&quot;;
           str += &quot;vertical-align:&quot;+(valign==&quot;Center&quot;?&quot;middle&quot;:valign) +&quot;;\n&quot;;
           if(topborder!=0)str += &quot;border-top:&quot;+topborder+&quot;pt solid &quot;+topbordercolor +&quot;;\n&quot;;
           if(rightborder!=0)str += &quot;border-right:&quot;+rightborder+&quot;pt solid &quot;+rightbordercolor +&quot;;\n&quot;;
           if(bottomborder!=0)str += &quot;border-bottom:&quot;+bottomborder+&quot;pt solid &quot;+bottombordercolor +&quot;;\n&quot;;
           if(leftborder!=0)str += &quot;border-left:&quot;+leftborder+&quot;pt solid &quot;+leftbordercolor +&quot;;\n&quot;;
           str += &quot;}\n&quot;;

        }
     str += &quot;--&gt;&quot;;     
     str+= &quot;&lt;/style&gt;&quot;;
     str +=&quot;&lt;/head&gt;&quot; 
     str+=&quot;&lt;body &gt;&quot;;
     str +=&quot;&lt;table border=0 cellpadding=0 cellspacing=0 style=&apos;border-collapse:collapse;table-layout:fixed;&apos;&gt;&quot;;
     var nrow; 
     var ncol; 
     var mrow; 
     var mcol; 
     for ( var i=row1;i&lt;=row2;i++) 
     { 
           str += &quot;&lt;tr height=&quot;+_this.GetRowHeight(sheet,i)+&quot;&gt;&quot;;         
           for ( var j=col1;j&lt;=col2;j++) 
           { 
                 var cellid =&quot;CELL_&quot;+sheet+&quot;_&quot;+i+&quot;_&quot;+j;
                 nrow = _this.GetAttribe(cellid , _this.ATTR_CELLMERGERROW );  
                 ncol = _this.GetAttribe(cellid , _this.ATTR_CELLMERGERCOL ); 
                 mcol = _this.GetAttribe(cellid , _this.ATTR_PARENTMERGER_CELLCOL );//得到父单元格的列 
                 mrow = _this.GetAttribe(cellid , _this.ATTR_PARENTMERGER_CELLROW);//得到父单元格的列 
                 var nWidth = _this.GetColWidth(sheet,j);
                 if(ncol&gt;1) // 如果有合并，要精确的列宽度
                       for( var col=j+1;col&lt;j+ncol;col++) nWidth+=_this.GetColWidth(sheet,col);

                 if ( mcol&gt;=0 || mrow&gt;=0 ) continue;// 说明是被合并
                 str+= &quot;&lt;td &quot;;
                 if ( nrow&gt;1 ) str+=&quot; rowspan=&quot;+nrow;
                 if ( ncol&gt;1 ) str+=&quot; colspan=&quot;+ncol;
                 str+= &quot; class=&quot;+_this.GetCellStyleid(sheet,i,j); 
                 str+= &quot; width=&quot;+nWidth ;
                 str+= &quot;&gt;&quot;+_this.GetCellText(0,i,j)+&quot;&lt;/td&gt;&quot;;
           }  
           str += &quot;&lt;/tr&gt;&quot;;  
     } 
     str+=&quot;&lt;/table&gt;&quot;; 
     str+=&quot;&lt;/body&gt;&lt;/html&gt;&quot;;
     //alert (str);
     return str; 
}

ROW.DATDSC:
SELECT CLASS ,FLAG ,RETURN ,FUNCTION ,NOTE  , NAME  ,	PARAMETER ,FUNCNOTE ,PARAMNOTE ,SAMPLE  ,RLSNO  ,LANG  ,SUBTYP   
FROM FUNCLIST
where  function like &apos;[%func]%&apos;
and name like &apos;[%fname]%&apos;
and funcnote like &apos;[%funcnote]%&apos;
