// 函数：thisOnpost_loaddata 
// 说明： 设置值
function _thisOnpost_loaddata(sheet)
{

        if ( sheet == 0 ) 
        {
        	try {HDRORDTL  = hdrordtl ;} catch ( e ) {HDRORDTL=&quot;1&quot;;}
        	try {PRJID = prjid ;} catch ( e ) {PRJID =&quot;&quot;;}
        	try {HDRGUID = hdrguid ;} catch ( e ) {HDRGUID =&quot;&quot;;}


 		_this.SetRowVisible(0,3,0);// 选择了项目了以后再显示
	        _this.SetRowVisible(0,4,0);
                sheetText =_this.AddSheet(&quot;事务正文&quot;);
                sheetGraph=_this.AddSheet(&quot;事务贴图&quot;);
               _this.SplitSheet(0,&quot;V&quot;,&quot;160&quot;);
               _this.SetToCodeEditor(&quot;&quot;,sheetText ,-1,-1,&quot;&quot;,&quot;&quot;,&quot;&quot;); //单元格设置为编辑框
               _this.SetToRichEdit(&quot;&quot;,sheetGraph,-1,-1,&quot;&quot;); //单元格设置为编辑框
