        } 
        window.status = &quot;新建事务&quot;;
        _this.SetToBoolBox(sheet,13,2);
        _this.SetToBoolBox(sheet,13,4);  
        _this.SetCellLock(sheet,3,8,1);
        _this.SetCellLock(sheet,3,2,1);
        _this.SetCellBkColor(0,-1,-1,255,255,255);
        _this.SetRowVisible(0,7,-1);
        _this.SetRowVisible(0,8,-1);
        _this.LoadScene(sheet,10);
         _this.SetToSelectBox(&quot;&quot;,sheet,3,2,&quot;V_USR&quot;,&quot;&quot;);  //分配人 
              QuerySQLDS(&quot;select sendto,endflg,retflg,toptyp,askflg from trktyp where id=&apos;&quot;+style+&quot;&apos; &quot;); 
              var sendto = _this.XMLDS_GetString(0,&quot;sendto&quot;); 
              var endflg = _this.XMLDS_GetString(0,&quot;endflg&quot;);
              var retflg = _this.XMLDS_GetString(0,&quot;retflg&quot;);        
              toptyp = _this.XMLDS_GetString(0,&quot;toptyp&quot;);
              var askflg = _this.XMLDS_GetString(0,&quot;askflg&quot;);
              toptyp = toptyp.RTrim();
              var showtopic = &quot;&quot;;
              sendto = sendto.RTrim();
              askflg = askflg.RTrim();                                     
              if ( toptyp != &quot;0&quot; )
              {
                    _this.SetToDateBox(&quot;&quot;,sheet,5,2,setDate());
             }
             if ( sendto == &quot;1&quot;)   //发送给任何多个人
             {
                  _this.SetMainCellRange(sheet,3,2,3,2);  

             }
             else if (sendto == &quot;2&quot;)  //发送给自己
             {
                   _this.SetRowVisible(sheet,3,-1);
                    _this.SetRowVisible(sheet,4,-1);
                  _this.SetCellText(sheet,3,2,G_USRID);  
                  _this.SetCellLock(sheet,3,2,1);  
             }
             else if ( sendto == &quot;3&quot;)  //发送给某个组织的所有人
             {      
                   //_this.SetCellLock(0,3,6,0); 
                   _this.SetTocomboBox(&quot;&quot;,sheet,3,2,setListCombox(&quot;v_xlsgrid&quot;));  
                   //_this.SetCellText(sheet,3,2,_this.GetCellText(sheet,3,6));
                   _this.SetCellLock(sheet,3,8,1);
             }
             if ( askflg == &quot;1&quot; ) //要录入事务提出方，提出人，提出时间
             {
                    _this.SetRowVisible(0,7,1);
                    _this.SetRowVisible(0,8,1);
                    _this.SetToDateBox(&quot;&quot;,0,7,7,setDate());

             }

        _this.SetToFileSelectBox(&quot;&quot;,0,9,2);
        _this.SetRowVisible(sheet,11,-1);
        _this.SetRowVisible(sheet,12,-1);
        _this.SetTocomboBox(&quot;trk_prio&quot;,sheet,1,8,setListCombox(&quot;v_prio&quot;));//设置优先级的下拉列表                     
        _this.SetCellText(sheet,1,8,&quot;0&quot;);  //默认是低，此处要放键值对的键，而不能放值         
        //where定义,
          var where =&quot;usrid=&apos;&quot;+G_USRID+&quot;&apos;&quot;;
          //alert ( _this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=v_prjusr&quot; ) );
          //alert(homeurl);homeurl=http://www.xlsgrid.net/xlsgrid/ROOT_XLSGRID/
          _this.SetCellText(0,1,2,PRJID );
          if ( HDRORDTL ==&quot;2&quot; ) {
          	          _thisOnCellModify(0,1,2,PRJID);
          }
          else {
	        var listid0 =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=v_prjusr&where= usrid=&apos;&quot;+G_USRID+&quot;&apos; and orgid=&apos;&quot;+G_ORGID+&quot;&apos;&quot; ),&quot;PRJID&quot;,&quot;PRJNAM&quot;);//查找出用户所属的项目      
          	_this.SetToComboBox(&quot;&quot;, sheet,1,2,listid0);//设置项目列表的语句
          }

//          where = &quot;selforg=&apos;&quot;+G_ORGID+&quot;&apos;&quot;;
//          listid0 = _this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=b_org&where=&quot;+where),&quot;AIMFORG&quot;,&quot;AIMORG&quot;);      
//	_this.SetToComboBox(&quot;&quot;, sheet,1,6,listid0);
	var orgid = &quot;&quot;;
	var orgname = &quot;&quot;;
	var list_org = _this.CreateListValue();
	QuerySQLDS(&quot;select org orgid,org orgname from (select selforg  org from b_org where selforg=&apos;&quot;+G_ORGID+&quot;&apos; union select aimorg  org from b_org where selforg=&apos;&quot;+G_ORGID+&quot;&apos;)&quot;);//查出组织	
	var rt = _this.XMLDS_GetRowCount();
	if ( rt &gt; 0 )
	{
		for ( var i=0;i&lt;rt;i++ )
		{
			orgid = _this.XMLDS_GetString(i,&quot;orgid&quot;);
			orgname = _this.XMLDS_GetString(i,&quot;orgname&quot;);
			 _this.SetListValue(list_org,orgid,orgname);
		}
	}
	_this.SetToComboBox(&quot;&quot;,sheet,1,6,list_org);
	_this.SetCellText(sheet,1,6,G_ORGID);
       // _this.SetTocomboBox(&quot;&quot;,sheet,3,7,setListCombox(&quot;v_xlsusr&quot;));//设置ORG=XLSGRID的下拉列表
        if ( edit == &quot;modify&quot; )  //修改事务
        {
              loadSheet();
        }
        Scroll1();
        Scroll2();

        if ( HDRORDTL  == &quot;2&quot; ) {

	        	// 发送给明细，表示处理事务
	        	_this.SetCellLock(0,1,2,1);
	        	_this.SetCellLock(0,1,6,1);
		_this.SetCellLock(0,1,8,1);
		_this.SetCellText(0,5,1,&quot;标题：&quot; );
		document.title = &quot;处理事务&quot;;
		_this.SetRowVisible(0,1,0);
        }
        else if  (HDRORDTL  == &quot;3&quot; ) {// 评论
	        	_this.SetRowVisible(0,3,0);
	        	_this.SetRowVisible(0,4,0);
	        	_this.SetCellText(0,5,1,&quot;评论标题：&quot; );

		document.title = &quot;发表评论&quot;;
		_this.SetRowVisible(0,1,0);
	}
	_this.SetCellText(0,5,8,&quot;2&quot; );// 修改事务状态,处理完，一旦保存后该标志修改trkhdr.stat


	// 项目
	try {
		var thisprj = prj;// prj 是传入的参数
		_this.SetCellText(0,1,2,thisprj);
		_thisOnCellModify(0,1,2,&quot;&quot;,thisprj)

	}
	catch ( e ) {}	
	// 回复事务或正在处理中
	try {
		var thisaction = action ;
		var thistousrid = tousrid;// tousrid是传入的参数
		if ( action == &quot;reply&quot; ) {//回复事务
			_this.SetCellText(0,3,2,thistousrid );
			_this.SetRowVisible(0,3,0);
			document.title = &quot;回复事务&quot;;
		} 
		else if ( action == &quot;waiting&quot;) {//处理中的事务
			_this.SetCellText(0,5,2,&quot;正在处理中&quot; );
			_this.SetCellText(1,-1,-1,&quot;我正在处理您的事务，估计处理时间是 &quot; );
			_this.SetCellText(0,3,2,thistousrid );
			_this.SetCellText(0,5,8,&quot;1&quot; );// 修改事务状态,处理中,一旦保存后该标志修改trkhdr.stat
			_this.SetRowVisible(0,3,0);
			document.title = &quot;设置该事务的状态为正在处理中...&quot;;

		}
	}
	catch ( e ) {}	

}


//================================================================// 
// 函数：save
// 说明：保存事务
// 参数：
// 返回：
// 样例：
// 作者：
// 创建日期：11/13/06 13:36:50
// 修改日志：
//================================================================// 
function save()   
{        

         var  s = &quot;&quot;;  //分配人
         var ff = true;  //判断分配人
         dtlusr = _this.GetCellText(sheet,3,2);//分配人的获取
        // alert (dtlusr);
        // return ;
         var file = _this.GetCellText(sheet,9,2);             //获取文件名    
         _this.SetCellText(sheet,11,2,file);  
         var imagenote = &quot;&quot;; //保存图片路径
         var imagefile = &quot;&quot;;
         var path1 = &quot;&quot;;//server端附件路径
         var path2 = &quot;&quot;;//server端图片路径
         var filename = &quot;&quot;;                  
         var aftername = &quot;&quot;;
         var sign = true; //判断附件是否上传成功
         var flag = true;//判断贴图是否上传成功
         var image = &quot;&quot;;
         var showimage = _this.GetCellShowText(sheetGraph,-1,-1);//贴图的2进制文件
         var aftername1  = &quot;&quot;;
         var filename1 = &quot;&quot;;
         //先行判断分配人
         if ( dtlusr == &quot;&quot; )
         {
                    dtlusr = G_USRID;
                    s = (dtlusr+&quot;,&quot;).split(&quot;,&quot;);
         }
         else
         {           
                     if ( dtlusr.lastIndexOf(&quot;,&quot;) == -1 )   
                     {        
                              //判断分配人是否输入错误
                             QuerySQLDS(&quot;select sendto from trktyp where id=&apos;&quot;+style+&quot;&apos; &quot;); //style是事务类型 2:一般事务 4:通知 31:技术交流....
                             var sendto =  _this.XMLDS_GetString(0,&quot;sendto&quot;); 
                             sendto = sendto.RTrim();
                             if ( sendto == &quot;3&quot; )
                             {
                                  QuerySQLDS(&quot;select id from v_usr where orgid=&apos;&quot;+dtlusr+&quot;&apos;&quot;);
                                  var rr = _this.XMLDS_GetRowCount();
                                  var dtlid = &quot;&quot;;
                                  if ( rr &gt; 0 )
                                  {
                                        for ( var i=0;i&lt;rr;i++)
                                        {
                                              dtlid += _this.XMLDS_GetString(i,&quot;id&quot;)+&quot;,&quot;;
                                        }
                                  }
                                 // s = dtlid.split(&quot;,&quot;);
                                  s = (dtlusr+&quot;,&quot;).split(&quot;,&quot;); 
                             }
                             else
                             {
                                   QuerySQLDS(&quot;select * from v_usr where id=&apos;&quot;+dtlusr+&quot;&apos;&quot;);
                                   var rowcount = _this.XMLDS_GetRowCount();
                                   if ( rowcount &lt; 1 )
                                   {
                                          alert (&quot;分配人输入错误！请重输&quot;);
                                          _this.SetCellText(sheet,3,2,&quot;&quot;);

                                          ff = false;

                                    }    
                                    s = (dtlusr+&quot;,&quot;).split(&quot;,&quot;);   
                              }     

                      }
                      else 
                      {
                              s = dtlusr.split(&quot;,&quot;);
                              //判断手写输入时，输入的分配人是否有错
                              for (var i=0;i&lt;s.length;i++)  
                              {
                                    if (s[i] == &quot;&quot;)
                                        break;
                                    QuerySQLDS(&quot;select * from v_usr where id=&apos;&quot;+s[i]+&quot;&apos;&quot;);
                                    var countrow = _this.XMLDS_GetRowCount();
                                    if ( countrow &lt; 1 )
                                    {
                                         ff = false;
                                    }
                              }
                              if ( !ff )
                              {      
                                    alert (&quot;分配人输入错误！请重输&quot;); 

                              }
                      }
         }                          
         //事务贴图
         if ( showimage.length &gt; 0)   
         {
                image = _this.GetCellText(sheetGraph,-1,-1); 
               // imagefile = _this.SaveTextFile(&quot;image.rtf&quot;,image,&quot;&quot;);  // 先在本地创建一个image.rtf文件
                imagefile = _this.SaveTextFileToTempDir(image);  //先把文件保存到临时文件夹
                imagenote = imagefile;            
                if ( imagefile != &quot;&quot; ) 
                {
                      aftername1 = &quot;.&quot; +getBySplit(imagefile,&quot;.&quot;,1); 
                      path2 = invokeOSFunc(&quot;file&quot;,&quot;&quot;); 
                      filename1 = getFilename();  
                      var temp_imagefile = _this.ZipFile(imagefile,&quot;&quot;); 
                      _this.OpenHttpRequest(&quot;uploadFile.sp&quot;,&quot;UPLOAD&quot;);  //打开上传页面  
                      _this.AddHttpRequestParam(&quot;uploadfile&quot;,temp_imagefile,1); 
                      _this.AddHttpRequestParam(&quot;path&quot;,path2,0); 
                      _this.AddHttpRequestParam(&quot;filename&quot;,filename1+aftername1,0);   //设定服务端的保存文件名 
                      var xml = _this.SendHttpRequest();                 //开始上传    
                      _this.CloseHttpRequest();     
                      var p = &quot;&quot;; 
                      if( xml == &quot;&quot; )    
                      {  
                            alert(&quot;操作错误,可能是网络连接错误.&quot;);  
                            flag = false; 
                      }  
                      else   
                      { 
                            _this.XMLDS_Parse(xml);   //解析XML字符串 
                            p = _this.XMLDS_GetString(1,&quot;code&quot;) ; 
                            if ( p == &quot;1&quot; )  
                            { 
                                 xml = &quot;保存成功&quot;;  
                                 flag = true; 
                            }     
                            else 
                            {  
                                  xml = &quot;上传图片失败&quot;; 
                                  flag = false;  
                                  alert (&quot;可能网络连接错误，&quot;+xml);
                                  _this.SetCellText(sheetGraph,-1,-1,&quot;&quot;); 

                            }  
                      } 
                }                
         }
         //事务附件
         if( file != &quot;&quot; || file.length != 0 ) 
         {
                path1 = invokeOSFunc(&quot;file&quot;,&quot;&quot;) ;         //服务端文件路径
                filename = getFilename();                     //此处要先取文件名，防止两次http请求冲突  
                _this.OpenHttpRequest(&quot;uploadFile.sp&quot;,&quot;UPLOAD&quot;);  //打开上传页面                                           
                aftername = &quot;.&quot;+getBySplit(file,&quot;.&quot;,1);           //文件后缀名 
               // aftername = &quot;.rar&quot;;
                var temp_file = _this.ZipFile(file,&quot;&quot;);           //把上传压缩文件放在一个临时目录
                _this.AddHttpRequestParam(&quot;uploadfile&quot;,temp_file,1);   //上传选定文件          
                _this.AddHttpRequestParam(&quot;path&quot;,path1,0);         //设定服务端的绝对保存路径，如 \tmp\,注意，路径后必须有符号 \   
                _this.AddHttpRequestParam(&quot;filename&quot;,filename+aftername,0);   //设定服务端的保存文件名
                var xml = _this.SendHttpRequest();                //开始上传    
                _this.CloseHttpRequest();    
                var p = &quot;&quot;;
                if( xml == &quot;&quot; )  
                { 
                      alert(&quot;操作错误,可能是网络连接错误.&quot;); 
                      sign = false;
                } 
                else 
                {
                      _this.XMLDS_Parse(xml);   //解析XML字符串
                      p = _this.XMLDS_GetString(1,&quot;code&quot;) ;
                      if ( p == &quot;1&quot; )
                      {
                           xml = &quot;保存成功&quot;; 
                           alert(&quot;上传成功&quot;);
                           sign = true;
                      }    
                      else
                      {
                            xml = &quot;上传附件失败&quot;;
                            sign = false; 
                            alert (&quot;可能是网络连接错误，&quot;+xml);
                            _this.SetCellText(0,9,2,&quot;&quot;);
                            _this.SetCellText(0,11,2,&quot;&quot;);

                      } 
                }
          }

          //保存
          if ( sign && flag && ff )
          {

                   var ret = 0;   
                   var count = 0;        
                   var mobile_sign = &quot;&quot;;
                   var msg_sign = &quot;&quot;;
                   if( _this.GetCellText(sheet,13,2) == 1 ) msg_sign = &quot;1&quot;;
                   else msg_sign = &quot;0&quot;;
                   if( _this.GetCellText(sheet,13,4) == 1 ) mobile_sign = &quot;1&quot;;  
                   else mobile_sign = &quot;0&quot;;                         
                   var project = _this.GetCellText(sheet,1,2); //直接取他的ID号 
                   if ( dtlusr == &quot;&quot; || dtlusr.length == 0 )
                   {
                         dtlusr = G_USRID;
                   }
                   var title = _this.GetCellText(sheet,5,2);  
                   var note = _this.GetCellShowText(sheetText,-1,-1);  ///可能不符
//                   alert(&quot;note=&quot;+note);
                   var prio = _this.GetCellText(sheet,1,8);
                   var proorg = _this.GetCellText(sheet,7,2);
                   var prousr = _this.GetCellText(sheet,7,6);
                   var filepath =path1 + filename;
                   var filenote = file ;
                   var crtusrorg = G_ORGID;
                   var prodat = _this.GetCellText(sheet,7,8);
                   if ( prodat == &quot;&quot; || prodat.length == 0 )
                   {
                   	prodat = setDate();
                   }
                   if ( (project == &quot;&quot; || project.length == 0)&&HDRORDTL!=&quot;3&quot;)//当为&quot;评论&quot;时可以没有项目名
                   {
                        alert (&quot;请输入项目名称&quot;);
                        return ; 
                    }
                    if ( title == &quot;&quot; || title.length == 0 ) 
                    { 
                         alert (&quot;请输入主题&quot;); 
                         return ;  
                     } 
                     QuerySQLDS(&quot;select name from v_usr where id=&apos;&quot;+G_USRID+&quot;&apos; and orgid=&apos;&quot;+G_ORGID+&quot;&apos;&quot;); 
                     var c = _this.XMLDS_GetRowCount();
                     var name = &quot;&quot;;
                     if( c &gt; 0 )
                     {
                           name = _this.XMLDS_GetString(0,&quot;name&quot;);

                     }                                   
                    var param = new Object();                               
                    param.project = project;
                    var p = note.lastIndexOf(&quot;\n\r&quot;);
                    if ( p != note.length-3 )
                    {
                          param.note = note+&quot;\n\r&quot;;
                    }
                    else
                    {      
                          param.note = note;
                    }
                    if ( toptyp != &quot;0&quot;)
                    {
                          var sql =  &quot; select decode(&apos;&quot;+toptyp+&quot;&apos;,&apos;1&apos;,to_char(to_date(&apos;&quot;+title+&quot;&apos;,&apos;yyyy-mm-dd&apos;),&apos;YYYY-MM-DD&apos;), &quot; + 
                                     &quot; &apos;2&apos;,to_char(to_date(&apos;&quot;+title+&quot;&apos;,&apos;yyyy-mm-dd&apos;),&apos;yyyy-ww&apos;),&apos;3&apos;,to_char(to_date(&apos;&quot;+title+&quot;&apos;,&apos;yyyy-mm-dd&apos;),&apos;YYYY-MM&apos;), &quot; + 
                                     &quot; &apos;4&apos;,to_char(to_date(&apos;&quot;+title+&quot;&apos;,&apos;yyyy-mm-dd&apos;),&apos;YYYY&apos;) ) showtopic from dual &quot;;  
                          QuerySQLDS(sql);  
                          var showtopic = _this.XMLDS_GetString(0,&quot;showtopic&quot;);
                          if ( toptyp == &quot;1&quot; ) showtopic += &quot;的日报&quot;;
                          else if (toptyp == &quot;2&quot; ) showtopic += &quot;周的周报&quot;;
                          else if (toptyp == &quot;3&quot; ) showtopic += &quot;月的月报&quot;;
                          else if ( toptyp == &quot;4&quot; ) showtopic += &quot;年的年报&quot;;
                          param.title = showtopic ;
                    }
                    else
                    {
                          param.title = title;  ////     
                    }                                 
                    param.crtusr = G_USRID; //修改过的，原来是name
                    param.prio= prio;				//优先级
                    param.crtusrorg = crtusrorg;		//用户所属组织
                    param.filepath = filepath+aftername;	//附件地址
                    param.filenote = filenote;			//附件
                    param.show = style;				//事务类型
                    param.mobile_sign = mobile_sign;		//短信通知
                    param.msg_sign = msg_sign;			//消息通知
                    param.proorg = proorg;			//提出组织
                    param.prousr = prousr;			//提出人
                    param.prodat = prodat;			//提出日期
                    param.imagepath = path2+filename1+aftername1;//贴图地址
                    param.imagenote = imagenote;		//贴图
                    param.aimorg = _this.GetCellText(sheet,1,6);//这个事务所属的组织
                    param.selforg = G_ORGID;			//全局变量
                    param.syt = G_SYTID;			//全局变量
                    param.acc = G_ACCID;			//全局变量
                    param.hdrguid = HDRGUID;			//事务打开事务的guid
                    param.tostat = _this.GetCellText(0,5,8);	//

			//alert(&quot;file=&quot;+file);
                    if ( edit == &quot;modify&quot; )
                    {
                          param.edit = &quot;modify&quot;;
                          param.id = id;
                          param.num = s.length-1;

                    }
                    else 
                    {
                          param.edit = &quot;save&quot;;
                    }
                    try 
                    {     

                          //当输入的分配人&gt;=1时，循环多次保存
                          for (var i=0;i&lt;s.length;i++)
                          {  
                                  if ( s[i] == &quot;&quot;)
                                         break ;
                                   //修改      

                                  if ( sendto == &quot;3&quot;)
                                  {                                        
                                        // param.dtlusr = dtlname;
                                            param.dtlusr = s[i];
                                            ret = invokeOSFuncExt(&quot;save&quot;+HDRORDTL,param) ; 
                                            count = ret++;
                                  }
                                  else 
                                  {    
                                       var sql = &quot;&quot;;
                                        if ( s[i] == &quot;xlsgrid&quot;)
                                        {
                                        	sql = &quot;select * from v_usr where id=&apos;&quot;+s[i]+&quot;&apos; and orgid=&apos;&quot;+G_ORGID+&quot;&apos;&quot;;
                                        }
                                        else    
                                        {                                                            
                                        	sql = &quot;select * from v_usr where id=&apos;&quot;+s[i]+&quot;&apos;&quot;;
                                        }
                                        QuerySQLDS(sql); 
                                        var cc = _this.XMLDS_GetRowCount();
                                        var dtlname = &quot;&quot;;
                                        if( cc &gt; 0 )
                                        {
                                                dtlname = _this.XMLDS_GetString(0,&quot;name&quot;);
                                                param.dtlusr = s[i];    ///x修改的，原来是到dtlname
                                                ret = invokeOSFuncExt(&quot;save&quot;+HDRORDTL,param) ;
                                                count ++; 
                                        } 
                                        if ( sendto == &quot;3&quot; )
                                        {
                                              count = 1;
                                        }
                                } 
                                //修改
                          }
                           alert ( &quot;已录入&quot;+count+&quot;条事务&quot;); 
                           return true ;   
                    }
                    catch(e)
                    {
                           alert (e);
                    }
             }                                   
 }


// 函数：savetrk
// 说明： 保存按钮
function savetrk()
{

                var str_user = _this.GetCellText(sheet,3,2);//获取分配人
                var user = str_user.split(&quot;,&quot;);               
                var flag_p = true;
                var temp = &quot;&quot;;
                for (var i=0;i&lt;user.length;i++)  //判断是否间隔重复输入，用于手写输入
                {

                       temp = user[i];
                       for ( var j=user.length;j&gt;i;j--)
                       {
                             if ( user[j] == temp )
                             {                                  
                                   flag_p = false;
                             }
                       }
                }
                if ( flag_p )
                {
                      if ( save() )
                      {
                              if ( dd == &quot;1&quot; || dd == &quot;2&quot;)  //项目管理里的东西,dd=1说明直接从项目管理传过来的，dd=0说明是从事务管理传过来的,dd=2说明是从工作报告过来的                                                             
                              {
                                     opener.location.reload();  //刷新  
                                     window.close();
                              }
                              else
                              {
                                    if ( edit == &quot;modify&quot;)
                                    {   
                                            opener.location.reload();  //刷新  
                                            window.close();
                                    }
                                    else
                                    {   
                                    	try {
                                            yyWindow.refreshSheet(); //opener关闭后，把opener对象保存到一个变量里 
                                        }
                                        catch( e ) {}
                                            //opener.refreshSheet();//如果operner未关闭，可以这样用
                                            window.close();  //父窗口关闭 
                                    }   
                              }                      
                      }
                }
                else
                {
                      alert (&quot;分配人重复输入，请重输！&quot;);
                }


}  
