function getFilename() 
{ 
      QuerySQLDS(&quot;select to_char(sysdate,&apos;yymmddhh24miss&apos;) dat from dual&quot;);  
      var count = _this.XMLDS_GetRowCount(); 
      var date = &quot;&quot;;      
      if ( count &gt; 0 )  
      { 
            date = _this.XMLDS_GetString(0,&quot;dat&quot;); 
      } 
      return  G_USRID+&quot;_&quot;+date; 
}
