// 函数：_thisOnCellModify
// 说明：事务类型单元格修改
// 参数：目前不用
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{          
        if ( sheet == 0 && row == 1 && col ==2 ) {      // 修改了项目以后

           QuerySQLDS(&quot;select sendto from trktyp where id=&apos;&quot;+style+&quot;&apos; &quot;); 
           var sendto = _this.XMLDS_GetString(0,&quot;sendto&quot;);
           if ( sendto == &quot;3&quot; )
           {
           	_this.SetCellLock(sheet,3,8,1);
           	_this.SetCellLock(sheet,3,2,0);
           }
           else
           {
                _this.SetCellLock(sheet,3,2,0);
            	_this.SetCellLock(sheet,3,8,0);
            }
            var prjid = _this.GetCellText(sheet,1,2);
            var where  = &quot;usrid=&apos;&quot;+G_USRID+&quot;&apos; and orgid=&apos;&quot;+G_ORGID+&quot;&quot;;
            var listid0 = &quot;&quot;; 

            	    where = &quot;prjid=&apos;&quot;+prjid+&quot;&apos;&quot;;
	            listid0 =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_PRJUSR&where=&quot;+where),&quot;USRID&quot;,&quot;USRNAM&quot;);//查找所属项目的人员
	            _this.SetRowVisible(0,3,1);
	            _this.SetRowVisible(0,4,1);

              	    _this.SetToComboBox(&quot;&quot;, sheet,3,8,listid0);

        }
        else if ( sheet == 0 && row == 3 && col ==8 )   //防止出现连续重复选中的情况，用户下拉表的选择
        {
              _this.SetCellLock(sheet,3,8,0);
              var id = _this.GetCellText(sheet,row,col);          
              var u = _this.GetCellText(sheet,3,2);             
              var s = u.split(&quot;,&quot;);
              var f = true;
              for (var i=0;i&lt;s.length;i++)
              {
                    if ( s[i] == id )
                    {
                          alert (&quot;分配人输入重复，请重输！&quot;);
                          f = false; 
                          break ;
                    }

              }
              if ( f ) 
              {
                    usr += id+&quot;,&quot;;
                   _this.SetCellText(sheet,3,2,usr);
              }
        }   
        if ( sheet == 0 && row == 1 &&col == 6)
        {
        var org = _this.GetCellText(sheet,1,6);
        if ( org != G_ORGID ) 
            {
            	var where =&quot;orgid=&apos;&quot;+org+&quot;&apos;&quot;;
            	var listid0 =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_USR&where=&quot;+where),&quot;ID&quot;,&quot;NAME&quot;);
            	 _this.SetToComboBox(&quot;&quot;, sheet,3,8,listid0);

            }

        }   
}

