            sendto = sendto.RTrim();
            retflg = retflg.RTrim();
            trsflg = trsflg.RTrim();
            toptyp = toptyp.RTrim();
            var id =&quot;&quot;;
            QuerySQLDS(&quot;select id ,org from v_usr where id=&apos;&quot;+G_USRID+&quot;&apos;&quot;); 
            var org = _this.XMLDS_GetString(0,&quot;org&quot;);
            id = _this.XMLDS_GetString(0,&quot;id&quot;);
            QuerySQLDS(&quot;select id  from v_usr where name=&apos;&quot;+crtusr+&quot;&apos;&quot;);  
            var crtid = _this.XMLDS_GetString(0,&quot;id&quot;); //创建人ID
            crtid = crtid.RTrim();
            id = id.RTrim();
            if ( sendto ==&quot;3&quot;) //组织  ,此时user=XLSGRID
            {
//
                  if ( org == user )
                  {
//                         url = &quot;ShowXlsGrid.sp?grdid=DTLTRK&project=&quot;+p+&quot;&num=&quot;+r+&quot;&user=&quot;+user+&quot;&retflg=&quot;+retflg+&quot;&trsflg=&quot;+trsflg+&quot;&crtid=&quot;+crtid;
//                         window.open( url ,&quot;事务处理&quot;,&quot;WIDTH=600,HEIGHT=600,left=0,top=0,toolbar=no,menu=no,status=yes&quot;);
                           alert (&quot;该事务不需回复！&quot;);
