function doSheet1(sheet)
{
     var sparam = new Object();
     sparam.prj = param_prj;
     sparam.create = param_create;
     sparam.usrid = G_USRID;
     var xml = invokeOSFuncExt(&quot;save&quot;,sparam) ;
     _this.SetMainCellRange(sheet,2,1,16,5);
     _this.SetXmlDS(sheet,2,1,16,5,xml);
}

ROW.DATDSC:
select distinct a.title,a.crtusr,to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) crtdat,a.id,a.show,&apos;show.sp?grdid=ITINFO&trkid=&apos; || a.id URL_title  
 from   
trkhdr a,trkdtl b,v_usr c,v_prj d ,trktyp e,prjusr pu  
where c.id=&apos;[%SYS_USRID]&apos; 
and a.show=e.id 
and a.guid=b.trkguid 
and pu.usr=c.guid  
and pu.prj=d.guid 
and a.dtlusr=c.name 
and a.project=d.id 
and  b.style=&apos;4&apos;  
and e.id in (&apos;4&apos;,&apos;16&apos;,&apos;17&apos;) 
and d.id like &apos;[%prj]%&apos; 
and a.crtusr like &apos;[%create]%&apos; 
and a.title like &apos;%[%title]%&apos; 
and e.id like &apos;[%trktyp]%&apos;  
order by crtdat desc 

