ROW.GRDJSDS_VALUE:
var sheet = 0;
var p = &quot;&quot;;//项目名称
var t = &quot;&quot;;//标题
var r = &quot;&quot;; //事务序号
var dd = &quot;0&quot;;
var a = true;
var rowno = &quot;&quot;;
//================================================================// 
// 函数：_thisOnpost_loaddata
// 说明：
// 参数：
// 返回：
// 样例：
// 作者：
// 创建日期：12/07/06 09:24:34
// 修改日志：
//================================================================// 
function _thisOnpost_loaddata(sheet)
{
         _this.SetCellBkColor(0,-1,-1,255,255,255); 
         _this.SetColVisible(sheet,11,-1);
         _this.SetFixedCol(sheet,1);
         _this.SetFixedRow(sheet,2);
         _this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW_CHECKBOX);
         _this.SetColVisible(sheet,1,-1);
         _this.SetColVisible(sheet,2,-1);
         _this.SetColVisible(sheet,4,-1);
         _this.SetColVisible(sheet,5,-1);
         _this.SetColVisible(sheet,6,-1);
         _this.SetColVisible(sheet,7,-1);
         _this.SetColVisible(sheet,10,-1);
         var row1 = _this.GetMainCellRangeRow1(sheet);
         var row2 = _this.GetMainCellRangeRow2(sheet);
         for (var i=row1;i&lt;=row2;i++)
         {
               var sname = _this.GetCellText(sheet,i,6);
               if ( sname ==&quot;已处理&quot;)
               {
                     _this.SetCellColor(sheet,i,6,0,0,255);
               }
         }
         if ( a )
         {
               document.all(&quot;searchbut&quot;).click();//加载页面时，就click一次 
               a = false;//防止多次点击
         }      
}


//================================================================//  
// 函数：_thisOnMouseLClick(
// 说明：                       
// 参数： 
// 返回： 
// 样例： 
// 作者： 
// 创建日期：12/07/06 09:32:45 
// 修改日志： 
//================================================================//  
function _thisOnMouseLClick(sheet,row,col) 
{
     var project = _this.GetCellText(sheet,row,1);
     var title = _this.GetCellText(sheet,row,3);
     if ( clickMianCell(sheet,row,col) )
     {
          if ( project == &quot;&quot; || project.length == 0 )
          {
                return ;
          }
          else
          {
                r = _this.GetCellText(sheet,row,2);  //         
                crtusr = _this.GetCellText(sheet,row,8);
                var show = _this.GetCellText(sheet,row,11);
                QuerySQLDS(&quot;select retflg,trsflg,toptyp from trktyp where id=&apos;&quot;+show+&quot;&apos;&quot;);
                var retflg = _this.XMLDS_GetString(0,&quot;retflg&quot;);
                var trsflg = _this.XMLDS_GetString(0,&quot;trsflg&quot;);
                var toptyp =  _this.XMLDS_GetString(0,&quot;toptyp&quot;);
                var sendto =  _this.XMLDS_GetString(0,&quot;sendto&quot;);
                trsflg = trsflg.RTrim();
                retflg = retflg.RTrim();
                toptyp = toptyp.RTrim();
                sendto = sendto.RTrim();
                var url = &quot;ShowXlsGrid.sp?grdid=M_TRKCHECK&project=&quot;+project+&quot;&num=&quot;+r+&quot;&user=&quot;+crtusr+&quot;&retflg=&quot;+retflg+&quot;&trsflg=&quot;+trsflg+&quot;&toptyp=&quot;+toptyp+&quot;&sendto=&quot;+sendto+&quot;&show=&quot;+show;
                //window.open( url ,&quot;事务查看&quot;,&quot;WIDTH=950,HEIGHT=480,left=0,top=0,toolbar=yes,menu=yes,location=yes,menubar=yes,status=yes&quot;); 
                window.open( url ,&quot;&quot;,&quot;WIDTH=1010,HEIGHT=650,left=0,top=0,toolbar=no,menu=no,location=no,menubar=no,status=yes&quot;); 

          }
     }
     var row1 = _this.GetMainCellRangeRow1(sheet);
     var row2 = _this.GetMainCellRangeRow2(sheet);
     if ( row&gt;=row1&&row&lt;=row2&&col==0 )  //点击单选框
     {  
           if ( _this.SetRowSelected(sheet,row,1) == 0 )    //=0被选中
          {
                 p = project;//赋值给全局变量
                 t = title; //赋值给全局变量
           }
           flag = true ;
           r = _this.GetCellText(sheet,row,2);  //事务序列号
           rowno = row;

     }

}

//================================================================//   
// 函数：deltrk 
// 说明：删除一条事务 
// 参数：
// 返回： 
// 样例：  
// 作者： 
// 创建日期：12/07/06 09:27:38 
// 修改日志：
//================================================================//  
function deltrk()
{
        if ( flag && (p != &quot;&quot; || p.length != 0))
        {
               QuerySQLDS(&quot;select name from v_usr where id=&apos;&quot;+G_USRID+&quot;&apos; and orgid=&apos;&quot;+G_ORGID+&quot;&apos;&quot;);
               var usrname = _this.XMLDS_GetString(0,&quot;name&quot;);
               if( _this.GetCellText(sheet,rowno,8) == usrname)
               {
	               if ( confirm(&quot;确认删除事务&lt;&quot;+p+&quot;&gt;吗？&quot;) == 1 )
	               {
	                     QuerySQLDS(&quot;delete from trkhdr where id=&apos;&quot;+r+&quot;&apos;&quot;);   
	                     QuerySQLDS(&quot;delete from trkdtl where id=&apos;&quot;+r+&quot;&apos;&quot;);                               
	                     document.all(&quot;searchbut&quot;).click();
	                     window.location.reload();
	                }
                }
                else
                {
                      alert (&quot;您不是该事务创建者，不能删除该事务！&quot;);
                      return ;
                }
        }
        else
        {
              alert (&quot;未选中事务！&quot;);
              return ;
        }

} 

//================================================================//  
// 函数：clickMianCell 
// 说明：判断鼠标点击的范围是主数据区 
// 参数： 
// 返回： bool 
// 样例： 
// 作者： 
// 创建日期：11/18/06 16:45:03 
// 修改日志：
//================================================================//  
function clickMianCell(sheet,row,col)
{
      return row&gt;=_this.GetMainCellRangeRow1(sheet)&&row&lt;=_this.GetMainCellRangeRow2(sheet)&&col&gt;=_this.GetMainCellRangeCol1(sheet)&&col&lt;=_this.GetMainCellRangeCol2(sheet);
}
