}

ROW.DATDSC:
select distinct d.name,a.id,a.title ,e.name 事务类型,a.dtlusr,a.stat ,a.prio ,a.crtusr,
to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) crtdat ,a.dtlusr 最后处理,a.show 
, &apos;entry.sp?grid=&trkid=&apos;||a.id URL_事务类型
from trkhdr a,trkdtl b,v_usr c,v_prj d,trktyp e where a.guid=b.trkguid(+) 
and c.id=&apos;[%SYS_USRID]&apos;
and a.show=e.id
and a.project=d.id
and b.crtusr=c.name  
and a.stat=&apos;已处理&apos;
and a.project like &apos;[%prj]%&apos; 
and a.title like &apos;%[%title]%&apos;
and a.prio like &apos;%[%PRIO]%&apos;
and e.finkey like &apos;%[%finkey]%&apos;
and e.id like &apos;[%trktyp]%&apos; 
order by crtdat desc
