//================================================================// 
// 函数：_thisOnpost_loaddata
// 说明：
// 参数：
// 返回：
// 样例：
// 作者：
// 创建日期：12/28/06 15:03:17
// 修改日志：
//================================================================// 
function _thisOnpost_loaddata(sheet)
{       
        if ( a )
        {
          showParamPanel(false); //隐藏“关闭查询条件”按钮
        _this.SetCellBkColor(0,-1,-1,255,255,255);    
        _this.SetFixedCol(sheet,1);
        _this.SetFixedRow(sheet,2);
        _this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW);
        _this.SetColVisible(sheet,5,-1);
        _this.SetColVisible(sheet,6,-1);
        _this.SetColVisible(sheet,4,-1);
	a = false;
   }
}

//================================================================// 
// 函数： _thisOnMouseLClick
// 说明：
// 参数：
// 返回：
// 样例：
// 作者：
// 创建日期：12/28/06 15:03:12
// 修改日志：
//================================================================// 
function _thisOnMouseLClick(sheet,row,col)
{

         var project = _this.GetCellText(sheet,row,1);
         var r = &quot;&quot;;
         var show = &quot;&quot;;
        if ( sheet == 0 )
        {
              var row1 = _this.GetMainCellRangeRow1(sheet);
              var row2 = _this.GetMainCellRangeRow2(sheet);
              if ( row&gt;=row1&&row&lt;=row2)  
              {
                          var show1 = _this.GetCellText(0,row,6);//事务类型
                          var id1 = _this.GetCellText(0,row,5);//事务编号
                          show = show1;
                          r = id1;                
               }
               else
                   return ;                   

        }
         QuerySQLDS(&quot;select project from trkhdr where id=&apos;&quot;+r+&quot;&apos;&quot;);
         var project = _this.XMLDS_GetString(0,&quot;project&quot;);
         QuerySQLDS(&quot;select retflg,trsflg,toptyp,sendto from trktyp where id=&apos;&quot;+show+&quot;&apos;&quot;);
         var retflg = _this.XMLDS_GetString(0,&quot;retflg&quot;);
         var trsflg = _this.XMLDS_GetString(0,&quot;trsflg&quot;);
         var toptyp = _this.XMLDS_GetString(0,&quot;toptyp&quot;);
         var sendto = _this.XMLDS_GetString(0,&quot;sendto&quot;);
         retflg = retflg.RTrim();
         trsflg = trsflg.RTrim();
         toptyp = toptyp.RTrim();
         sendto = sendto.RTrim();
         var url = &quot;ShowXlsGrid.sp?grdid=M_TRKCHECK&project=&quot;+project+&quot;&num=&quot;+r+&quot;&user=&quot;+crtusr+&quot;&retflg=&quot;+retflg+&quot;&trsflg=&quot;+trsflg+&quot;&toptyp=&quot;+toptyp+&quot;&show=&quot;+show+&quot;&sendto=&quot;+sendto;
         // alert (url);
         window.open( url ,&quot;&quot;,&quot;WIDTH=1010,HEIGHT=650,left=0,top=0,toolbar=no,menu=no,location=no,menubar=no,status=yes&quot;); 
}


//================================================================// 
// 函数：clickMianCell
// 说明：防止点出数据区以外
// 参数：
// 返回：true or false 
// 样例：
// 作者：
// 创建日期：01/08/07 13:49:22
// 修改日志：
//================================================================// 
function clickMianCell(sheet,row,col)
{
      return row&gt;=_this.GetMainCellRangeRow1(sheet)&&row&lt;=_this.GetMainCellRangeRow2(sheet)&&col&gt;=_this.GetMainCellRangeCol1(sheet)&&col&lt;=_this.GetMainCellRangeCol2(sheet);
}

