ROW.GRDOSDS_VALUE:
var pubpack = new JavaPackage ( &quot;com.xlsgrid.net.pub&quot; );//加载类包  
var grdpack = new JavaPackage ( &quot;com.xlsgrid.net.grd&quot; );  

function save() 
{
      var ret = 0;
      var db = null;
      var ds = null;
      var sql = &quot;&quot;;
      try
      { 
             db = new pubpack.EADatabase();
             sql = &quot;select distinct a.title,a.crtusr,to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) crtdat,a.id,a.show from  &quot;+ 
			&quot;trkhdr a,trkdtl b,v_usr c,v_prj d ,trktyp e,prjusr pu  &quot;+
			&quot;where c.id=&apos;&quot;+usrid+&quot;&apos; &quot;+
			&quot;and a.show=e.id &quot;+
			&quot;and a.guid=b.trkguid &quot;+
			&quot;and pu.usr=c.guid  &quot;+
			&quot;and pu.prj=d.guid &quot;+
			&quot;and a.dtlusr=c.name &quot;+
			&quot;and a.project=d.id &quot;+
			&quot;and  b.style=&apos;未处理&apos;  &quot;+
			&quot;and e.id in (&apos;4&apos;,&apos;5&apos;,&apos;16&apos;,&apos;17&apos;) &quot;+
			&quot;and d.id like &apos;&quot;+prj+&quot;%&apos; &quot;+
			&quot;and a.crtusr like &apos;&quot;+create+&quot;%&apos; &quot;+
//			&quot;and a.title like &apos;%[%title]%&apos; &quot;+
//			&quot;and e.id like &apos;[%trktyp]%&apos;  &quot;+
			&quot;order by crtdat desc &quot;; 
			//throw new Exception (sql);
			 ds = db.QuerySQL(sql);
			 return ds.GetXml();

       }
	     catch(e)
	      {
	            throw e;
	      }
	      finally
	      {
	            db.Close(); 
      }       
}

ROW.DATDSC:
select distinct d.name prj,a.id,a.title ,e.name 事务类型 ,c.name,b.style,a.prio,a.crtusr,to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) crtdat,
a.dtlusr 最后处理,a.show  from 
trkhdr a,trkdtl b ,v_usr c,v_prj d ,trktyp e where a.guid=b.trkguid
and c.id=&apos;[%SYS_USRID]&apos; 
and a.show =e.id 
and b.crtusr=c.name
and a.project=d.name
and ( b.style=&apos;未处理&apos; or b.style = &apos;处理中&apos;)
and a.prio like &apos;[%PRIO]%&apos;
and d.id like &apos;[%prj]%&apos;
and a.crtusr like &apos;%[%create]%&apos;
and a.title like &apos;%[%title]%&apos;
and e.finkey like &apos;%[%finkey]%&apos;
and e.id like &apos;[%trktyp]%&apos; 

union

select distinct d.name prj,a.id,a.title ,e.name 事务类型 ,c.name,b.style,a.prio,a.crtusr,to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) crtdat,
a.dtlusr 最后处理,a.show  from 
trkhdr a,trkdtl b,v_xlsgrid c,v_prj d ,trktyp e
where c.id=(select org from v_usr where id=&apos;[%SYS_USRID]&apos;)
and a.show=e.id
and a.dtlusr=c.id
and a.project=d.name
and b.style=&apos;未处理&apos;  
and a.guid=b.trkguid
and a.prio like &apos;[%PRIO]%&apos;
and d.id like &apos;[%prj]%&apos;
and a.crtusr like &apos;%[%create]%&apos;
and a.title like &apos;%[%title]%&apos;
and e.finkey like &apos;%[%finkey]%&apos;
and e.id like &apos;[%trktyp]%&apos; 
order by crtdat desc


-- guid in ( select prjusr.prj from prjusr,usr where usr.guid=prjusr.usr and usr.id=&apos;[%SYS_USRID]&apos; )

select * from  
(select rownum num1, a.title aa,a.crtusr bb ,to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) cc ,a.prio dd,a.id ee ,a.show ff 
 from  
