select * from  
(select rownum num1,p.* from (
(select distinct a.title aa,a.crtusr bb ,to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) cc ,a.prio dd,a.id ee ,a.show ff 
