and e.finkey like &apos;%[%finkey]%&apos; 
and e.id like &apos;[%trktyp]%&apos; 
order by cc desc
) p))p1

full outer join

(select &apos;&apos;,rownum num2,q.* from (
(select distinct a.title,a.crtusr,to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) crtdat,a.id,a.show from  
trkhdr a,trkdtl b,v_usr c,v_prj d ,trktyp e,prjusr pu 
where c.id=&apos;[%SYS_USRID]&apos;
and a.show=e.id
and a.guid=b.trkguid
--and pu.usr=c.guid 
--and pu.prj=d.guid
and a.dtlusr=c.name
and a.project=d.id
and  b.style=&apos;未处理&apos; 
and e.id in (&apos;4&apos;,&apos;5&apos;,&apos;16&apos;,&apos;17&apos;)
and d.id like &apos;[%prj]%&apos;
and a.crtusr like &apos;[%create]%&apos;
