              return ;
         }          
}  

//================================================================//  
// 函数：_thisOnMouseLClick
// 说明：
// 参数：
// 返回：
// 样例：
// 作者：
// 创建日期：12/07/06 16:57:17 
// 修改日志：
//================================================================//   
function _thisOnMouseLClick(sheet,row,col)
{

     var project = _this.GetCellText(sheet,row,1);
     var title = _this.GetCellText(sheet,row,3);
     var retitle = _this.GetCellText(sheet,row,4);
     var row1 = _this.GetMainCellRangeRow1(sheet);
     var row2 = _this.GetMainCellRangeRow2(sheet);
     if ( clickMianCell(sheet,row,col) )
     {
          if ( project == &quot;&quot; || project.length == 0 )
          {
                return ;
          }
          else
          {
                r = _this.GetCellText(sheet,row,2);  //事务序列号
                QuerySQLDS(&quot;select project,crtusr,stat from trkhdr where id=&apos;&quot;+r+&quot;&apos;&quot;);
                var project = _this.XMLDS_GetString(0,&quot;project&quot;);
                var crtusr = _this.XMLDS_GetString(0,&quot;crtusr&quot;);
                var stat = _this.XMLDS_GetString(0,&quot;stat&quot;);
                var show = _this.GetCellText(sheet,row,11);
                QuerySQLDS(&quot;select retflg,trsflg,toptyp,sendto from trktyp where id=&apos;&quot;+show+&quot;&apos;&quot;);
                var retflg = _this.XMLDS_GetString(0,&quot;retflg&quot;);
                var trsflg = _this.XMLDS_GetString(0,&quot;trsflg&quot;);
                var toptyp =  _this.XMLDS_GetString(0,&quot;toptyp&quot;);
                var sendto = _this.XMLDS_GetString(0,&quot;sendto&quot;);
                trsflg = trsflg.RTrim();
                retflg = retflg.RTrim();
                toptyp = toptyp.RTrim();
                sendto = sendto.RTrim();
               // var url = &quot;ShowXlsGrid.sp?grdid=TRKCHECK&project=&quot;+project+&quot;&num=&quot;+r+&quot;&user=&quot;+crtusr+&quot;&show=&quot;+show;
                var url = &quot;ShowXlsGrid.sp?grdid=TRKCHECK&project=&quot;+project+&quot;&num=&quot;+r+&quot;&user=&quot;+crtusr+&quot;&retflg=&quot;+retflg+&quot;&trsflg=&quot;+trsflg+&quot;&toptyp=&quot;+toptyp+&quot;&show=&quot;+show+&quot;&sendto=&quot;+sendto+&quot;&stat=&quot;+stat;
                window.open( url ,&quot;&quot;,&quot;WIDTH=1010,HEIGHT=650,left=0,top=0,toolbar=no,menu=no,location=no,menubar=no,status=yes&quot;); 

          }
     }



     var project = _this.GetCellText(sheet,row,1); 
     var title = _this.GetCellText(sheet,row,3);  
     var retitle = _this.GetCellText(sheet,row,4);  
     var row1 = _this.GetMainCellRangeRow1(sheet);  
     var row2 = _this.GetMainCellRangeRow2(sheet); 
     if ( row&gt;=row1&&row&lt;=row2&&col==0 )  //点击单选框 
     {                                       
           if ( _this.SetRowSelected(sheet,row,1) == 0 )    //=0被选中  
           { 
                 p = project;//赋值给全局变量 
                 t = title; //赋值给全局变量  
           } 
           flag = true ; 
           r = _this.GetCellText(sheet,row,2);  //事务序列号 
           rowno = row;//rowno保存行号
     } 

}

//================================================================// 
// 函数：close
// 说明：关闭事务
// 参数：
// 返回：
// 样例：
// 作者：
// 创建日期：12/15/06 15:16:37
// 修改日志：
//================================================================// 
function close()
{
       r = _this.GetCellText(sheet,rowno,2);
      QuerySQLDS(&quot;select project,crtusr,stat from trkhdr where id=&apos;&quot;+r+&quot;&apos;&quot;);
      var project = _this.XMLDS_GetString(0,&quot;project&quot;);
      var crtusr = _this.XMLDS_GetString(0,&quot;crtusr&quot;);
      var stat = _this.XMLDS_GetString(0,&quot;stat&quot;);
      var show_id = _this.GetCellText(sheet,rowno,11);
      QuerySQLDS(&quot;select endflg from trktyp where id=&apos;&quot;+show_id+&quot;&apos;&quot;);
      var endflg = _this.XMLDS_GetString(0,&quot;endflg&quot;);
      endflg = endflg.RTrim();
      if ( endflg == &quot;0&quot; )  //只能由事务创建者关闭事务
      {
            if ( p == &quot;&quot; || p.length == 0 )
            {
                  alert (&quot;未选中事务！&quot;);
                  return ;
            }
           else
           {
                  if ( crtusr == G_USRID )
                  {
                        QuerySQLDS(&quot;update trkhdr set stat=&apos;3&apos; where  id=&apos;&quot;+r+&quot;&apos;&quot;);
                        QuerySQLDS(&quot;update trkdtl set style=&apos;3&apos; where id=&apos;&quot;+r+&quot;&apos;&quot;);
                        document.all(&quot;searchbut&quot;).click();   
                        alert (&quot;该事务已关闭！&quot;);
                        window.location.reload();     
                  }  
                  else 
                  {
                        alert (&quot;该事务不是由您创建，不能关闭该事务！&quot;);
                        return ;
                  }
           }
     }
     else if ( endflg ==&quot;1&quot;)
     {
           alert (&quot;不可关闭事务！&quot;);
           return ;
     }
}
//================================================================// 
// 函数：edit
// 说明：修改未被处理的事务
// 参数：
// 返回：
// 样例：
// 作者：
// 创建日期：12/18/06 13:14:38
// 修改日志：
//================================================================// 
function edit()
{
       r = _this.GetCellText(sheet,rowno,2);
       QuerySQLDS(&quot;select project,crtusr,stat from trkhdr where id=&apos;&quot;+r+&quot;&apos;&quot;);
       var stat = _this.XMLDS_GetString(0,&quot;stat&quot;); 
      if ( p == &quot;&quot; || p.length == 0 )
      {
            alert (&quot;未选中事务！&quot;);
            return ;
      }     
      else 
      {
            if ( stat == &quot;0&quot; )  //只有新建的未被处理的事务才可修改
            {
                  var estyle = _this.GetCellText(sheet,rowno,4);
                  QuerySQLDS(&quot;select id from v_trktyp where name=&apos;&quot;+estyle+&quot;&apos;&quot;);
                  var show_id = _this.XMLDS_GetString(0,&quot;id&quot;); 
                  var url = &quot;ShowXlsGrid.sp?grdid=HDRTRK&edit=&quot;+&quot;modify&quot;+&quot;&id=&quot;+r+&quot;&style=&quot;+show_id+&quot;&dd=&quot;+dd; 
                  window.open( url ,&quot;新建事务&quot;,&quot;WIDTH=900,HEIGHT=650,left=0,top=0,toolbar=no,menu=no,status=yes&quot;);  
            }
            else
                  alert (&quot;本事务已被处理，不能修改！&quot;);
      }         
}
//================================================================//  
// 函数：clickMianCell 
// 说明：判断鼠标点击的范围是主数据区 
// 参数： 
// 返回： bool 
// 样例： 
// 作者： 
// 创建日期：11/18/06 16:45:03 
// 修改日志：
//================================================================//  
function clickMianCell(sheet,row,col)
{
      return row&gt;=_this.GetMainCellRangeRow1(sheet)&&row&lt;=_this.GetMainCellRangeRow2(sheet)&&col&gt;=_this.GetMainCellRangeCol1(sheet)&&col&lt;=_this.GetMainCellRangeCol2(sheet);
}
