// 函数：expTable
// 说明：相同事务类型的事务导出到一张表格
// 参数：
// 返回：
// 样例：
// 作者：
// 创建日期：02/09/07 09:06:59
// 修改日志：
//================================================================// 
function expTable()
{
     var r1 = _this.GetMainCellRangeRow1(sheet);
     var r2 = _this.GetMainCellRangeRow2(sheet);
     var trkid = &quot;&quot;;
     var trktyp = &quot;&quot;;
     var trkusr = &quot;&quot;;
     var temp = &quot;&quot;;
     for (var i=r1;i&lt;=r2;i++)
     {
            trkid += _this.GetCellText(sheet,i,2)+&quot;,&quot;;//事务编号
            trktyp += _this.GetCellText(sheet,i,4)+&quot;,&quot;;//事务类型
            trkusr += _this.GetCellText(sheet,i,5)+&quot;,&quot;;//处理人

     }
//     temp = trkusr.split(&quot;,&quot;);
//     for ( var i=0;i&lt;temp.length;i++)
//     {        
//           if ( temp[i+1] == &quot;&quot; ) break;
//           if ( temp[i] != temp[i+1])
//           {
//                 alert (temp[i]);
//                 alert (&quot;不能导出！&quot;);
//                 return ;
//           }
//     }
     var index = trkid.lastIndexOf(&quot;,&quot;);  //得到最后一个“，”的位置值
     trkid = trkid.substring(0,index);   
     var url = &quot;ShowXlsGrid.sp?grdid=EXPFORM&quot;+&quot;&trkid=&quot;+trkid;
     window.open( url ,&quot;事务导出&quot;,&quot;WIDTH=1000,HEIGHT=800,left=0,top=0,toolbar=no,menu=no,location=no,menubar=no,status=yes&quot;); 

}

ROW.DATDSC:
select distinct d.name prj,a.id,a.title,e.name 事务类型 ,decode(a.dtlusr,&apos;[%SYS_ORGID]&apos;,&apos;[%SYS_ORGID]&apos;,p.name ) dtlusr,
f.name stat,n.name prio,c.name crtusr,to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) crtdat,
decode(a.dtlusr,&apos;[%SYS_ORGID]&apos;,&apos;[%SYS_ORGID]&apos;,p.name ) 最后处理,a.show   
from trkhdr a,trkdtl b ,v_usr c ,v_prj d ,trktyp e ,v_stat f ,v_usr p,v_usr m ,v_prio n where a.guid=b.trkguid(+) 
and c.id=lower(&apos;[%SYS_USRID]&apos;) 
and c.orgid=&apos;[%SYS_ORGID]&apos;
--and p.id=&apos;[%SYS_USRID]&apos;
and a.show=e.id
and a.project=d.id
and p.orgid=decode(a.aimorg,&apos;&apos;,&apos;[%SYS_ORGID]&apos;,a.aimorg)
and a.crtusr = c.id
and (a.dtlusr=p.id or a.dtlusr=&apos;[%SYS_ORGID]&apos;)
and a.selforg=c.orgid
--and a.selforg=p.orgid--有跨组织发事务的情况
and f.id=a.stat
and a.prio=n.id
and a.prio like &apos;%[%prio]%&apos;
and d.id like &apos;[%prj]%&apos;   
and a.stat like &apos;%[%s]%&apos;
and a.title like &apos;%[%title]%&apos;
and e.finkey like &apos;%[%finkey]%&apos;
and e.id like &apos;[%trktyp]%&apos; 
--and a.dtlusr like &apos;[%dtlusr]%&apos;
--and a.dtlusr like decode(&apos;%&apos;,&apos;[%dtlusr]%&apos;,&apos;[%dtlusr]%&apos;,(select name from v_usr where id like &apos;[%dtlusr]%&apos;))
order by crtdat desc
