//================================================================//  
// 函数：commit 
// 说明：点击保存并传参 
// 参数：
// 返回： 
// 样例： 
// 作者： 
// 创建日期：01/08/07 12:22:17 
// 修改日志： 
//================================================================//  
function commit()
{
        var sign = false;
        var style = &quot;&quot;;
        var guid = &quot;&quot;
        var name = _this.GetCellText(sheet,1+1,2);
        var note = _this.GetCellText(sheet,3+1,2);
        var finkey = _this.GetCellText(sheet,5+1,2);
        var trsflg =  _this.GetCellText(sheet,7+1,2);
        var sendto = _this.GetCellText(sheet,9+1,2);
        var endflg = _this.GetCellText(sheet,11+1,2);
        var retflg = _this.GetCellText(sheet,13+1,2);
        var toptyp = _this.GetCellText(sheet,15+1,2);
        var param = new Object();
        param.name = name;
        param.note = note;
        param.finkey = finkey;
        param.trsflg = trsflg;
        param.sendto = sendto;
        param.endflg = endflg;
        param.retflg = retflg;
        param.toptyp = toptyp;
        param.crtusr = G_USRID;
        try  
        {
             guid = invokeOSFuncExt(&quot;save&quot;,param) ; 
             sign = true;
             //alert (guid);
        }
        catch(e)
        {
             alert (e);
        }
        if ( sign )
        {
              QuerySQLDS(&quot;select id from trktyp where guid=&apos;&quot;+guid+&quot;&apos;&quot;); 
              style = _this.XMLDS_GetString(0,&quot;id&quot;);
              var url =  &quot;ShowXlsGrid.sp?grdid=HDRTRK&style=&quot;+style+&quot;&edit=&quot;+edit;
              var child = window.open( url ,&quot;新建事务&quot;,&quot;WIDTH=900,HEIGHT=600,left=0,top=0,toolbar=no,menu=no&quot;);
              child.yyWindow = opener;
              window.close();

        }

}
