function process()
{ 
      if ( p == &quot;&quot; || p.length == 0 )
      {
            alert (&quot;未选中事务！&quot;);
            return ;
      }
      else 
      {
            var user = _this.GetCellText(sheet,rowno,10);//最后处理人      
            var crtusr = _this.GetCellText(sheet,rowno,8);//创建人
            var show = _this.GetCellText(sheet,rowno,11); //事务类型
            var url = &quot;&quot;;
            QuerySQLDS(&quot;select retflg,trsflg,toptyp,sendto from trktyp where id=&apos;&quot;+show+&quot;&apos;&quot;);
            var toptyp = _this.XMLDS_GetString(0,&quot;toptyp&quot;);
