                                url = &quot;ShowXlsGrid.sp?grdid=DTLTRK&project=&quot;+p+&quot;&num=&quot;+r+&quot;&user=&quot;+user+&quot;&retflg=&quot;+retflg+&quot;&trsflg=&quot;+trsflg+&quot;&crtid=&quot;+crtid;                        
