//================================================================// 
// 函数：edit
// 说明：修改日报
// 参数：
// 返回：
// 样例：
// 作者：
// 创建日期：03/23/07 16:48:30
// 修改日志：
//================================================================// 
function edit()
{
       var dd = &quot;2&quot;;
      if ( p == &quot;&quot; || p.length == 0 )
      {
            alert (&quot;未选中事务！&quot;);
            return ;
      }     
      else 
      {
            if ( _this.GetCellText(sheet,rowno,6) == &quot;未处理&quot; )  //只有新建的未被处理的事务才可修改
            {
                  var estyle = _this.GetCellText(sheet,rowno,4);
                  QuerySQLDS(&quot;select id from v_trktyp where name=&apos;&quot;+estyle+&quot;&apos;&quot;);
                  var show_id = _this.XMLDS_GetString(0,&quot;id&quot;); 
                  var url = &quot;ShowXlsGrid.sp?grdid=HDRTRK&edit=&quot;+&quot;modify&quot;+&quot;&id=&quot;+r+&quot;&style=&quot;+show_id+&quot;&dd=&quot;+dd; 
                  window.open( url ,&quot;新建事务&quot;,&quot;WIDTH=900,HEIGHT=650,left=0,top=0,toolbar=no,menu=no,status=yes&quot;);  
            }
            else
                  alert (&quot;本事务已被处理，不能修改！&quot;);
      }

}
///
function Scroll1() 
{ 

	var len=msg.length;  
	window.status = msg.substring(0,seq+1);  
	seq++; 
	if(seq &gt;= len) 
	{ 
	       seq = spacelen; 
	       window.setTimeout(&quot;Scroll2();&quot;,interval );  
	}  
	else  
	      window.setTimeout(&quot;Scroll1();&quot;,interval ); 
} 

function Scroll2() 
{ 
	var out = &quot; &quot;; 
	for(i=1;i&lt;=spacelen/space10.length;i++) 
	{
	      out += space10;
	 }       
	out = out+msg; 
	len = out.length; 
	window.status = out.substring(seq,len); 
	seq++; 
	if (seq &gt;=len )  {seq = 0;} 
	 window.setTimeout(&quot;Scroll2();&quot;,interval ); 
}

