ROW.DATDSC:
select distinct d.name prj,a.id,a.title ,e.name 事务类型 ,c.name,b.style,a.prio,a.crtusr,to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) crtdat,
a.dtlusr 最后处理,a.show  from 
trkhdr a,trkdtl b ,v_usr c,v_prj d ,trktyp e where a.guid=b.trkguid
and c.id=&apos;[%SYS_USRID]&apos; 
and a.show =e.id 
and b.crtusr=c.name
and a.project=d.name
and ( b.style=&apos;未处理&apos; or b.style = &apos;处理中&apos;)
and a.prio like &apos;[%PRIO]%&apos;
and d.id like &apos;[%prj]%&apos;
and a.crtusr like &apos;%[%create]%&apos;
and a.title like &apos;%[%title]%&apos;
and e.finkey like &apos;%[%finkey]%&apos;
and e.id like &apos;[%trktyp]%&apos; 

union

select distinct d.name prj,a.id,a.title ,e.name 事务类型 ,c.name,b.style,a.prio,a.crtusr,to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) crtdat,
a.dtlusr 最后处理,a.show  from 
trkhdr a,trkdtl b,v_xlsgrid c,v_prj d ,trktyp e
where c.id=(select org from v_usr where id=&apos;[%SYS_USRID]&apos;)
and a.show=e.id
and a.dtlusr=c.id
and a.project=d.name
and b.style=&apos;未处理&apos;  
and a.guid=b.trkguid
and a.prio like &apos;[%PRIO]%&apos;
and d.id like &apos;[%prj]%&apos;
and a.crtusr like &apos;%[%create]%&apos;
and a.title like &apos;%[%title]%&apos;
and e.finkey like &apos;%[%finkey]%&apos;
and e.id like &apos;[%trktyp]%&apos; 
order by crtdat desc

select * from  
(select rownum num1, a.title aa,a.crtusr bb ,to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) cc ,a.prio dd,a.id ee ,a.show ff 
 from  
trkhdr a,trkdtl b ,v_usr c,v_prj d ,trktyp e where a.guid=b.trkguid 
and c.id=&apos;[%SYS_USRID]&apos;  
and a.show =e.id  
and b.crtusr=c.name  
and a.project=d.name 
and ( b.style=&apos;未处理&apos; or b.style = &apos;处理中&apos;) 
and e.id&lt;&gt;&apos;5&apos;
and a.prio like &apos;[%PRIO]%&apos;
and d.id like &apos;[%prj]%&apos;
and a.crtusr like &apos;[%create]%&apos;
and a.title like &apos;%[%title]%&apos;
and e.finkey like &apos;%[%finkey]%&apos; 
order by cc desc
) p,

(select &apos;&apos;,rownum num2,a.title,a.crtusr,to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) crtdat,a.id,a.show   from  
trkhdr a,trkdtl b,v_usr c,v_prj d ,trktyp e
where c.id=&apos;[%SYS_USRID]&apos;
and a.show=e.id
and a.guid=b.trkguid
and a.dtlusr=c.name
and a.project=d.name
and  b.style=&apos;未处理&apos; 
and e.id=&apos;5&apos;
and d.id like &apos;[%prj]%&apos;
and a.crtusr like &apos;[%create]%&apos;
and a.title like &apos;%[%title]%&apos;
order by crtdat desc
) q
where p.num1=q.num2(+)


union

select * from  
(select rownum num1, a.title aa,a.crtusr bb ,to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) cc ,a.prio dd,a.id ee ,a.show ff 
 from  
trkhdr a,trkdtl b ,v_usr c,v_prj d ,trktyp e where a.guid=b.trkguid 
and c.id=&apos;[%SYS_USRID]&apos;  
and a.show =e.id  
and b.crtusr=c.name  
and a.project=d.name 
and ( b.style=&apos;未处理&apos; or b.style = &apos;处理中&apos;) 
and e.id&lt;&gt;&apos;5&apos;
and a.prio like &apos;[%PRIO]%&apos;
and d.id like &apos;[%prj]%&apos;
and a.crtusr like &apos;[%create]%&apos;
and a.title like &apos;%[%title]%&apos;
and e.finkey like &apos;%[%finkey]%&apos; 
order by cc desc
) a,

(select &apos;&apos;,rownum num2,a.title,a.crtusr,to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) crtdat,a.id,a.show   from  
trkhdr a,trkdtl b,v_usr c,v_prj d ,trktyp e
where c.id=&apos;[%SYS_USRID]&apos;
and a.show=e.id
and a.guid=b.trkguid
and a.dtlusr=c.name
and a.project=d.name
and  b.style=&apos;未处理&apos; 
and e.id=&apos;5&apos;
and d.id like &apos;[%prj]%&apos;
and a.crtusr like &apos;[%create]%&apos;
and a.title like &apos;%[%title]%&apos;
order by crtdat desc
) b
where a.num1(+)=b.num2

select * from  
(select rownum num1,p.* from (
(select  a.title aa,a.crtusr bb ,to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) cc ,a.prio dd,a.id ee ,a.show ff 
 from  
trkhdr a,trkdtl b ,v_usr c,v_prj d ,trktyp e where a.guid=b.trkguid 
and c.id=&apos;[%SYS_USRID]&apos;  
and a.show =e.id  
and b.crtusr=c.name  
and a.project=d.name 
and ( b.style=&apos;未处理&apos; or b.style = &apos;处理中&apos;) 
and e.id not in (&apos;4&apos;,&apos;5&apos;,&apos;6&apos;,&apos;7&apos;,&apos;8&apos;,&apos;9&apos;,&apos;10&apos;)
and a.prio like &apos;[%PRIO]%&apos;
and d.id like &apos;[%prj]%&apos;
and a.crtusr like &apos;[%create]%&apos;
and a.title like &apos;%[%title]%&apos;
and e.finkey like &apos;%[%finkey]%&apos; 
and e.id like &apos;[%trktyp]%&apos; 
order by cc desc
) p))p1

full outer join

(select &apos;&apos;,rownum num2,q.* from (
(select a.title,a.crtusr,to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) crtdat,a.id,a.show   from  
trkhdr a,trkdtl b,v_usr c,v_prj d ,trktyp e
where c.id=&apos;[%SYS_USRID]&apos;
and a.show=e.id
and a.guid=b.trkguid
and a.dtlusr=c.name
and a.project=d.name
and  b.style=&apos;未处理&apos; 
and e.id in (&apos;4&apos;,&apos;5&apos;,&apos;6&apos;,&apos;7&apos;,&apos;8&apos;,&apos;9&apos;,&apos;10&apos;)
and d.id like &apos;[%prj]%&apos;
and a.crtusr like &apos;[%create]%&apos;
and a.title like &apos;%[%title]%&apos;
and e.id like &apos;[%trktyp]%&apos; 
order by crtdat desc
) q)) q1
on (p1.num1=q1.num2)
order by p1.num1,q1.num2

select distinct d.name prj,a.id,a.title ,e.name 事务类型 ,c.name,b.style,a.prio,a.crtusr,to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) crtdat,
a.dtlusr 最后处理,a.show  from 
trkhdr a,trkdtl b ,v_usr c,v_prj d ,trktyp e where a.guid=b.trkguid
--and  c.id like decode(&apos;[%SYS_USRID]&apos;,&apos;scott&apos;,&apos;%%&apos;,&apos;[%SYS_USRID]&apos;)
and c.id like decode (&apos;[%SYS_USRID]&apos;,&apos;scott&apos;,&apos;%%&apos;,&apos;lyj&apos;,&apos;%%&apos;,&apos;[%SYS_USRID]&apos;)
--and c.id=&apos;[%SYS_USRID]&apos;
and a.show =e.id 
and b.crtusr=c.name
and a.project=d.id
and b.style=&apos;未处理&apos;
and a.prio like &apos;[%PRIO]%&apos;
and a.stat like &apos;[%s]%&apos;
and d.id like &apos;[%prj]%&apos;
and e.id in (&apos;6&apos;,&apos;7&apos;,&apos;8&apos;,&apos;9&apos;,&apos;10&apos;)
and a.crtusr like &apos;[%create]%&apos;
and a.title like &apos;%[%title]%&apos;
and e.finkey like &apos;[%finkey]%&apos;
and e.id like &apos;[%trktyp]%&apos; 
order by crtdat desc
