//================================================================// 
// 函数：_thisOnpost_loaddata
// 说明：
// 参数：
// 返回：
// 样例：
// 作者：
// 创建日期：12/28/06 15:03:17
// 修改日志：
//================================================================// 
function _thisOnpost_loaddata(sheet)
{       
        _this.SetFixedCol(sheet,1);
        _this.SetFixedRow(sheet,2);
        _this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_SINGLEROW);
        _this.SetCellBkColor(0,-1,-1,255,255,255); 
        _this.SetColVisible(0,_this.GetColCount(0)-1,0);

}

//================================================================// 
// 函数： _thisOnMouseLClick
// 说明：
// 参数：
// 返回：
// 样例：
// 作者：
// 创建日期：12/28/06 15:03:12
// 修改日志：
//================================================================// 
function _thisOnMouseLClick(sheet,row,col)
{
	if ( row &gt; 0 ) 
	   	window.location =&quot;Layout.sp?id=trkquery&trkguid=&quot;+_this.GetCellText(sheet,row,_this.GetColCount(0)-1)+&quot;&showhead=n&quot; ;
}   
function _thisOnMouseDClick(sheet,row,col)
{
	if ( row &gt; 0 ) 
	   	window.open(&quot;Layout.sp?id=trkquery&trkguid=&quot;+_this.GetCellText(sheet,row,_this.GetColCount(0)-1)+&quot;&showhead=n&quot; );
}   
