//================================================================// 
// 函数：clickMianCell
// 说明：防止点出数据区以外
// 参数：
// 返回：true or false 
// 样例：
// 作者：
// 创建日期：01/08/07 13:49:22
// 修改日志：
//================================================================// 
function clickMianCell(sheet,row,col)
{
      return row&gt;=_this.GetMainCellRangeRow1(sheet)&&row&lt;=_this.GetMainCellRangeRow2(sheet)&&col&gt;=_this.GetMainCellRangeCol1(sheet)&&col&lt;=_this.GetMainCellRangeCol2(sheet);
}

