function load_num()
{
     var sql = &quot;select * from (select distinct a.title aa,a.crtusr bb ,to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) cc ,a.prio dd,a.id ee ,a.show ff &quot;+
		&quot; from  &quot;+
		&quot;trkhdr a,trkdtl b ,v_usr c,v_prj d ,trktyp e,prjusr pu where a.guid=b.trkguid  &quot;+
		&quot;and c.id=&apos;&quot;+G_USRID+&quot;&apos;   &quot;+
		&quot;and c.orgid=&apos;&quot;+G_ORGID+&quot;&apos;&quot;+
		&quot;and a.show =e.id   &quot;+
		&quot;and b.crtusr=c.name  &quot;+ 
		&quot;and a.project=d.id  &quot;+
		&quot;and ( b.style=&apos;未处理&apos; or b.style = &apos;处理中&apos;)  &quot;+ 
		&quot;and e.id not in (&apos;4&apos;,&apos;5&apos;,&apos;6&apos;,&apos;7&apos;,&apos;8&apos;,&apos;9&apos;,&apos;10&apos;,&apos;16&apos;,&apos;17&apos;) &quot;+
		&quot;order by cc desc )&quot;; 

        QuerySQLDS(sql);            

        msgnum = _this.XMLDS_GetRowCount();	 
        var sss = meg();       
        window.document.write(sss);

}

ROW.GRDOSDS_VALUE:
