ROW.DATDSC:
select a.title|| &apos;  &apos;||c.name ||&apos; &apos;||to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) name,substr(a.note,1,100) note ,
a.guid ,a.id,&apos;show.sp?grdid=&apos;||a.id url
 from  
trkhdr a,v_usr c ,trktyp e,v_usr cc
where 
a.dtlusr=c.id  and a.AIMORG=c.orgid and a.crtusr=cc.id  and a.AIMORG=cc.orgid 
and c.id=lower(&apos;[%SYS_USRID]&apos;)   
and c.orgid=&apos;[%SYS_ORGID]&apos;
and a.show =e.id   
and a.stat&lt;&gt;&apos;3&apos; -- 0：新建，1：处理中，2：已处理，3：处理完，4：未处理
and e.id not in (&apos;4&apos;,&apos;5&apos;,&apos;6&apos;,&apos;7&apos;,&apos;8&apos;,&apos;9&apos;,&apos;10&apos;,&apos;16&apos;,&apos;17&apos;) 
and a.prio like &apos;[%PRIO]%&apos; 
and a.crtusr like &apos;[%create]%&apos; 
and a.title like &apos;%[%title]%&apos; 
and e.finkey like &apos;%[%finkey]%&apos;  
and e.id like &apos;[%trktyp]%&apos;  
order by to_char(a.crtdat,&apos;yyyy-mm-dd hh24:mi:ss&apos;) desc
