function Response()
{
	var func=pubpack.EAFunc.NVL(request.getParameter(&quot;func&quot;),&quot;&quot;);//获取执行的函数名称如:save
	var data=pubpack.EAFunc.NVL(request.getParameter(&quot;_this&quot;),&quot;&quot;);//获取提交的数据.是一个xml
