ROW.GRDJSDS_VALUE:
var sheet = 0;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SetRowVisible(sheet,4,-1);
	_this.SetRowVisible(sheet,16,-1);
	_this.SetCellColor(sheet,10,5,120,120,120);
	_this.SetCellColor(sheet,11,5,120,120,120);
	_this.SetCellColor(sheet,12,5,120,120,120);

	if (typeof guid == &quot;undefined&quot;) {
		try { _this.SetCellText(sheet,3,2,refmod); } catch ( e ) { }
		try { _this.SetCellText(sheet,4,2,selsytid); } catch ( e ) { }
		try { _this.SetCellText(sheet,5,2,topicid); } catch ( e ) { }
		try { if (name != &quot;right&quot;) _this.SetCellText(sheet,6,2,name); } catch ( e ) { }
		try { _this.SetCellText(sheet,7,2,lognam); } catch ( e ) { }
		try { _this.SetCellText(sheet,8,2,val1); } catch ( e ) { }
		try { _this.SetCellText(sheet,9,2,val2); } catch ( e ) { }
		try { _this.SetCellText(sheet,13,2,refid); } catch ( e ) { }
	}

	var modguid = _this.GetCellText(sheet,3,2);
	var modname = _this.GetCellShowText(sheet,3,2);
	var thissytid = _this.GetCellText(sheet,4,2);
	var topicid = _this.GetCellText(sheet,5,2);
	var reftopid = _this.GetCellText(sheet,13,2);

	//计算主题的编号
	var modid = &quot;&quot;;
	var ss = modname.split(&quot;-&quot;);
	if (ss.length &gt; 1) modid = ss[0];

	if (topicid == &quot;&quot; && modid != &quot;&quot;) {
		if (reftopid == &quot;&quot;) _sql.querytods(&quot;getmaxid1&quot;,&quot;thissytid=&quot;+thissytid+&quot;&refmod=&quot;+modguid);
		else _sql.querytods(&quot;getmaxid2&quot;,&quot;thissytid=&quot;+thissytid+&quot;&refmod=&quot;+modguid+&quot;&refid=&quot;+reftopid);

		var maxid = &quot;&quot;;	
		var newid = &quot;&quot;;

		if (_this.XMLDS_GetRowCount() &gt; 0) {
			maxid = _this.XMLDS_GetString(0,&quot;ID&quot;);
			ss = maxid.split(&quot;-&quot;);
			if (ss.length &gt; 1) {
				ss[ss.length-1] = 1*ss[ss.length-1]+1;
				for (var i=0;i&lt;ss.length;i++) {
					if (i != 0) newid += &quot;-&quot;;
					newid += ss[i];
				}
			} else {	//没有找到最大值，从1开始
				if (reftopid == &quot;&quot;) newid = modid +&quot;-1&quot;;
				else newid = reftopid +&quot;-1&quot;;
			}
		}
		_this.SetCellText(sheet,5,2,newid);
	}

	var thisguid = &quot;&quot;;
	try { thisguid = guid; }catch ( e ) { }

	if ( thisguid == &quot;&quot; ) {
		_this.SetToStandardCell(sheet,1,5);
		_this.SetCellText(sheet,1,5,&quot;&quot;);
		_this.SetCellLeftBorder(sheet,1,5,0);	_this.SetCellRightBorder(sheet,1,5,0);	_this.SetCellBottomBorder(sheet,1,5,0);	_this.SetCellTopBorder(sheet,1,5,0);
		_this.SetToStandardCell(sheet,1,6);
		_this.SetCellText(sheet,1,6,&quot;&quot;);
		_this.SetCellLeftBorder(sheet,1,6,0);	_this.SetCellRightBorder(sheet,1,6,0);	_this.SetCellBottomBorder(sheet,1,6,0);	_this.SetCellTopBorder(sheet,1,6,0);
	} else {
		var xmlmenu = &quot;&lt;item id=\&quot;com_addnew_level\&quot; name=\&quot;本级别\&quot;/&gt;&lt;item id=\&quot;com_addnew_nextlevel\&quot; name=\&quot;下级别\&quot;/&gt;&quot;;
		_this.SetToButton(&quot;com_new&quot;,sheet,1,5,&quot;添加新主题&quot;,&quot;&quot;,xmlmenu,0,-1,-1);
		 xmlmenu = &quot;&lt;item id=\&quot;com_copynew_level\&quot; name=\&quot;本级别\&quot;/&gt;&lt;item id=\&quot;com_copynew_nextlevel\&quot; name=\&quot;下级别\&quot;/&gt;&quot;;
		_this.SetToButton(&quot;com_copy&quot;,sheet,1,6,&quot;复制并添加&quot;,&quot;&quot;,xmlmenu,0,-1,-1);
	}

	var list = _this.CreateListValue();
	_this.SetListValue(list,&quot;0&quot;,&quot;不显示&quot;);
	_this.SetListValue(list,&quot;1&quot;,&quot;显示&quot;);
	_this.SetToComboBox(&quot;&quot;,sheet,15,2,list);
	_this.SetToComboBox(&quot;&quot;,sheet,16,2,list);

	_sql.querytods(&quot;charttype&quot;,&quot;&quot;);
	var list =_this.CreateListValue();
	for ( var i=0;i&lt;_this.XMLDS_GetRowCount();i++ ) {
		_this.SetListValue(list,_this.XMLDS_GetString(i,&quot;ID&quot;),_this.XMLDS_GetString(i,&quot;NAME&quot;));
	}
	_this.SetToComboBox(&quot;&quot;,sheet,17,2,list);
}

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	var modguid = _this.GetCellText(sheet,3,2);
	if (modguid == &quot;&quot;) {
		alert(&quot;请先选择分析模型！&quot;);
		return;
	}

	if ((row == 8 || row == 9 || row == 18) && col == 6) {
		_this.QuerySQLDS(&quot;select * from dim_model where guid=&apos;&quot;+modguid+&quot;&apos;&quot;);
		var table_name = _this.XMLDS_GetString(0,&quot;SOURCEDS&quot;);

		var selval = _this.GetCellText(sheet,row,2);
		var table_name1 = &quot;DIM_TARGET&quot;;
		if (row == 9 || row == 18) table_name1 = &quot;DIM_DIM&quot;;
		var url =&quot;show.sp?grdid=BISelCol&table_name=&quot;+table_name+&quot;&table_name1=&quot;+table_name1+&quot;&modguid=&quot;+modguid+&quot;&selval=&quot;+selval;
		var ret = window.showModalDialog(url);
		if (ret != &quot;&quot; && ret != null) _this.SetCellText(sheet,row,2,ret);
	} 
}

//点击菜单项
function _thisOnMenuItemSelect(sheet,row,col,menuitemid)
{
	if (menuitemid == &quot;com_copynew_level&quot;) {	//添加同级新主题
		window.location = &quot;show.sp?grdid=BITopic&refid=&quot;+_this.GetCellText(sheet,13,2)+&quot;&selsytid=&quot;+_this.GetCellText(sheet,4,2)+&quot;&refmod=&quot;+_this.GetCellText(sheet,3,2)+&quot;&val1=&quot;+_this.GetCellText(sheet,8,2)+&quot;&val2=&quot;+_this.GetCellText(sheet,9,2)+&quot;&name=&quot;+_this.GetCellText(sheet,6,2)+&quot;&lognam=&quot;+_this.GetCellText(sheet,7,2);
	}
	if (menuitemid == &quot;com_copynew_nextlevel&quot;) {	//添加新主题
		window.location = &quot;show.sp?grdid=BITopic&refid=&quot;+_this.GetCellText(sheet,5,2)+&quot;&selsytid=&quot;+_this.GetCellText(sheet,4,2)+&quot;&refmod=&quot;+_this.GetCellText(sheet,3,2)+&quot;&val1=&quot;+_this.GetCellText(sheet,8,2)+&quot;&val2=&quot;+_this.GetCellText(sheet,9,2)+&quot;&name=&quot;+_this.GetCellText(sheet,6,2)+&quot;&lognam=&quot;+_this.GetCellText(sheet,7,2);
	}
	if (menuitemid == &quot;com_addnew_level&quot;) {		//添加同级主题
		window.location = &quot;show.sp?grdid=BITopic&refid=&quot;+_this.GetCellText(sheet,13,2)+&quot;&selsytid=&quot;+_this.GetCellText(sheet,4,2)+&quot;&refmod=&quot;+_this.GetCellText(sheet,3,2);
	}
	if (menuitemid == &quot;com_addnew_nextlevel&quot;) {	//添加子主题
		window.location = &quot;show.sp?grdid=BITopic&refid=&quot;+_this.GetCellText(sheet,5,2)+&quot;&selsytid=&quot;+_this.GetCellText(sheet,4,2)+&quot;&refmod=&quot;+_this.GetCellText(sheet,3,2)+&quot;&topicid=&quot;+_this.GetCellText(sheet,5,2)+&quot;_1&quot;;
	}
}

//保存单据数据后
function _thisOnaftersave(success)
{
	try { parent.frames(&quot;left&quot;).f_refresh(); } catch ( e ) { }
	return success;
}

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if (id == &quot;F_run_deletebutton1&quot;) {
		if (window.confirm(&quot;删除本单据，是否继续？&quot;)) {
			var ret = _sql.run(&quot;delete&quot;,&quot;guid=&quot;+guid);
			if (ret == 1) alert(&quot;删除成功！&quot;);
			else alert(&quot;删除失败！&quot;);
		}

		try { parent.location.href = &quot;framelayout.sp?leftgrdid=BITreeDef&rightflowid=&rightflowsyt=&quot;; } catch ( e ) { }
		return 1;
	}
}

//单据列表加载完毕
function _thisOnpostloadListBill(sheet)
{
	try {
		var v_mod = selectBillGrid.CreateListValueByXMLDS(selectBillGrid.HttpCall(&quot;XmlDB.sp?bind=V_DIMMODEL&quot;),&quot;ID&quot;,&quot;NAME&quot;);
		for (var i=selectBillGrid.GetMainCellRangeRow1(sheet);i&lt;=selectBillGrid.GetMainCellRangeRow2(sheet);i++) {
			selectBillGrid.SetToComboBox(&quot;&quot;,sheet,i,5,v_mod);
			selectBillGrid.SetCellHAlign(sheet,i,5,0);
		}
	} catch ( e ) { }
}

