						&lt;td id=topTd width=420 align=&apos;center&apos;&gt;
							&lt;table border=&apos;0&apos; cellspacing=&apos;0&apos; cellpadding=&apos;0&apos; width=&apos;100%&apos; height=&apos;100%&apos;&gt;
							&lt;tr&gt;
							&lt;td id=RPoint class=navPoint bgColor=#EEEEEE onclick=switchRBar() style=&apos;border-left: 1px solid #CCC; border-right: 1px solid #CCC; width: 3pt; vertical-align: middle;&apos;&gt;
								4
							&lt;/td&gt;
							&lt;td id=rightTd width=100%&gt;
								&lt;table border=&apos;0&apos; cellspacing=&apos;0&apos; cellpadding=&apos;0&apos; width=&apos;100%&apos; height=&apos;100%&apos;&gt;
								&lt;tr&gt;&lt;td height=30 align=center background=xlsgrid/images/xlsgrid/tab.bg.off.grid.gif&gt;
									更换图型：
									&lt;select id=&apos;chgchart&apos; size=&apos;1&apos; onchange=&apos;f_chgchart();&apos; style=&apos;border: 1px solid #808080&apos;&gt;
										&quot;+optionstr+&quot;
									&lt;/select&gt;
									&nbsp;
									&lt;a href=&apos;javascript:zoomchart();&apos;&gt;放大图&lt;/a&gt;
								&lt;/td&gt;&lt;/tr&gt;
								&lt;tr&gt;&lt;td height=50% valign=top&gt;
									&lt;div id=&apos;chartdiv1&apos; align=&apos;center&apos;&gt; &nbsp;&lt;/div&gt;
								&lt;/td&gt;&lt;/tr&gt;
								&lt;tr&gt;&lt;td height=50% valign=top&gt;
									&lt;hr&gt;
									&lt;div id=&apos;detail&apos; align=&apos;center&apos; style=&apos;height:95%;padding:5px;overflow:auto;&apos;&gt;&nbsp;&lt;/div&gt;
								&lt;/td&gt;&lt;/tr&gt;
								&lt;/table&gt;
							&lt;/td&gt;
							&lt;/tr&gt;
							&lt;/table&gt;
						&lt;/td&gt;
						&lt;/tr&gt;
						&lt;/table&gt;
					&lt;/td&gt;
					&lt;/tr&gt;
					&lt;/table&gt;
				&lt;/body&gt;
			&lt;/html&gt;
		&quot;;
	}
	catch ( ee ) {
		db.Rollback();
		throw new pub.EAException ( ee.toString() );
	}
	finally {
		if (db!=null) db.Close();
	}
	return ret;
}

ROW.DATDSC:
select rownum seq,id,name from dim_topic
 where refmod=&apos;[%FORMGUID]&apos; and sytid=&apos;[%sytid]&apos; and refid=&apos;[%topic]&apos;
 order by id

select a.hdim,b.id from dim_topic a,dim_dim b
 where a.refmod=b.refmod and a.sytid=&apos;[%sytid]&apos;
   and a.id=&apos;[%topic]&apos; and a.hdim like &apos;%&apos;||b.id||&apos;%&apos;
