function genstr(str){

	var sql=&quot;&quot;;
	var ds=&quot;&quot;;
	var db=null;
	db = new pubpack.EADatabase();
	var layid=&quot;&quot;;
	try{
		sql=&quot;
			SELECT B.* FROM LAYOBJ A ,LAYOBJDTL B  
			WHERE A.GUID=B.FORMGUID 
			AND A.ID =&apos;CHISMOVEPIC&apos; AND A.CLS=&apos;CHIS&apos;and B.id =&apos;&quot;+str+&quot;&apos;
		&quot;;
		ds=db.QuerySQL(sql);
		if(ds.getRowCount()==1){
			layid=ds.getStringAt(0,&quot;VAL&quot;);
			return layid; 
		}
	}
	catch(e){ throw new Exception(e);}
	if(db!=null){db.Close();}
}

