		       to_date(to_char(sysdate, &apos;yyyy-mm-dd hh24:mi&apos;), &apos;yyyy-mm-dd hh24:mi&apos;)))
		   and (usernum &lt;&gt; 0 or usernumaf &lt;&gt; 0)
		   and ((morning &lt;&gt; &apos;-&apos; and morning is not null) or
		       (afternoon &lt;&gt; &apos;-&apos; and afternoon is not null)) 
	) and c.isol=&apos;1&apos; AND a.iskf&lt;&gt;&apos;1&apos;
	&quot;;

	ds=db.QuerySQL(sql);

