function getRoom(str) {
	//str=\&quot;16C09F8E7AD7D593E050007F01007F8D\&quot;;

	var socket = new WebSocket(\&quot;ws://114.80.114.43:8000/\&quot;);
	socket.onopen = function(event) {
		socket.send(JSON.stringify({
			&apos;EVENT&apos;: &apos;CONROOM&apos;,
			&apos;GUID&apos;:  str
		}));
	};
	socket.onmessage = function(event) {
		var mData = JSON.parse(event.data);
		switch(mData.event) {
			case &apos;CONROOM&apos;:
			var room = mData.ROOM;
			if(room == null || room ==undefined || room ==\&quot;\&quot;)
			{
			alert(&apos;医生还未开始会诊！&apos;);
			return ;
			}
			else{
			window.parent.location.href=&apos;L.sp?id=CHIS_NODEJSSICK&USERID=&ro=&apos;+room;
			//openWindow(&apos;L.sp?id=CHIS_NODEJSSICK&USERID=029&ro=&apos;+room);
			}
			break;
		}
	};
}
var playbox = (function(){
	//author:eric_wu
	var _playbox = function(){
		var that = this;
		that.box = null;
		that.player = null;
		that.src = null;
		that.on = false;
		//
		that.autoPlayFix = {
			on: true,
			//evtName: (\&quot;ontouchstart\&quot; in window)?\&quot;touchend\&quot;:\&quot;click\&quot;
			evtName: (\&quot;ontouchstart\&quot; in window)?\&quot;touchstart\&quot;:\&quot;mouseover\&quot;

		}

	}
	_playbox.prototype = {
		init: function(box_ele){
			this.box = \&quot;string\&quot; === typeof(box_ele)?document.getElementById(box_ele):box_ele;
			this.player = this.box.querySelectorAll(\&quot;audio\&quot;)[0];
			this.src = this.player.src;
			this.init = function(){return this;}
			this.autoPlayEvt(true);
			return this;
		},
		play: function(){
			if(this.autoPlayFix.on){
				this.autoPlayFix.on = false;
				this.autoPlayEvt(false);
			}
			this.on = !this.on;
			if(true == this.on){
				this.player.src = this.src;
				this.player.play();
			}else{
				this.player.pause();
				this.player.src = null;
			}
			if(\&quot;function\&quot; == typeof(this.play_fn)){
				this.play_fn.call(this);
			}
		},
		handleEvent: function(evt){
			this.play();
		},
		autoPlayEvt: function(important){
			if(important || this.autoPlayFix.on){
				document.body.addEventListener(this.autoPlayFix.evtName, this, false);
			}else{
				document.body.removeEventListener(this.autoPlayFix.evtName, this, false);
			}
		}
	}
	//
	return new _playbox();
})();

playbox.play_fn = function(){
	this.box.className = this.on?\&quot;btn_music on\&quot;:\&quot;btn_music\&quot;;
}
