ROW.GRDJSDS_VALUE:
var sheet=0;
var stat = &quot;0&quot;;
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SetColVisible(0,1,-1);
	_this.SetColVisible(0,3,-1);
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this.SELECTFLAG_MULTIROW_CHECKBOX );  
	stat = document.all.STAT.value;

}

function CHECK()
{
	operate(&quot;CHECK&quot;);
}

function UNCHECK()
{
	operate(&quot;UNCHECK&quot;);
}

function operate(o)
{
	var xml = getXml(0,o);
//	alert(xml);
  	//POST方式调用网络OS函数
	var param = new Object();
	param.xmlstr = xml;
	param.o = o;
//	alert(param.o);
	param.usrid = G_USRID;
	param.acc = G_ACCID;
	var msg = &quot;&quot;;
	if(o==&quot;CHECK&quot;&&stat==&quot;2&quot;){
		alert(&quot;单据均已审核!&quot;);
		return;
	}
	if(o==&quot;UNCHECK&quot;&&stat==&quot;1&quot;){
		alert(&quot;单据均未审核!&quot;);
		return;
	}
	try {
//		alert(&quot;xxx&quot;);
		msg = invokeOSFuncExt(&quot;operate&quot;,param);
		alert(&quot;成功操作&quot;+msg+&quot;张单据&quot;);
//		window.location.reload();
	}
	catch (e) {
		alert ( e );
	}
}

//获得xml
function getXml(sheet,o){
	var row1 = _this.GetMainCellRangeRow1(sheet);
	var row2 = _this.GetMainCellRangeRow2(sheet);
	var xml=&quot;&lt;?xml version=&apos;1.0&apos; encoding=&apos;UTF-8&apos;?&gt;&quot;+&quot;\n&quot;;
	var k=0;
	xml = xml+&quot;&lt;ROWSET&gt;&quot;+&quot;\n&quot;
	for(var row=row1;row&lt;=row2;row++){

		if(_this.GetRowSelected(sheet,row)==1){
			var stat =_this.GetCellText(sheet,row,11);
			if((o==&quot;CHECK&quot;&&stat==&quot;未审&quot;)||(o==&quot;UNCHECK&quot;&&stat==&quot;已审&quot;)){
				xml = xml+&quot;&lt;ROW NUM=&apos;&quot;+k+&quot;&apos;&gt;&quot;+&quot;\n&quot;;
				var guid = _this.GetCellText(sheet,row,1);
				var biltyp = _this.GetCellText(sheet,row,3);
				var bilid = _this.GetCellText(sheet,row,5);

				xml = xml+&quot;&lt;guid&gt;&quot;+guid+&quot;&lt;/guid&gt; \n&quot;;
				xml = xml+&quot;&lt;biltyp&gt;&quot;+biltyp+&quot;&lt;/biltyp&gt; \n&quot;;
				xml = xml+&quot;&lt;bilid&gt;&quot;+bilid+&quot;&lt;/bilid&gt; \n&quot;;
				xml = xml+&quot;&lt;/ROW&gt; \n&quot;
				k++;
			}

		}
	}
	xml = xml + &quot;&lt;/ROWSET&gt; \n&quot;;
	return xml;
}



//双击选择主数据区行
function _thisOnDblClickMainCellRangeRow(sheet,row)
{
  var biltyp = _this.GetCellText(sheet,row,3);
  var bilid = _this.GetCellText(sheet,row,5);
  window.open(&quot;show.sp?grdid=&quot;+biltyp+&quot;&ACC=&quot;+G_ACCID+&quot;&bilid=&quot;+bilid);
}

ROW.GRDOSDS_VALUE:
var pubpack = new JavaPackage ( &quot;com.xlsgrid.net.pub&quot; );
var grd = new JavaPackage ( &quot;com.xlsgrid.net.grd&quot; );
// 客户端param传入的参数可以直接使用

function operate(){
//	throw new pubpack.EAException(xmlstr);
	var db = null;

	try{
		var xmlds = new pubpack.EAXmlDS(xmlstr);
		var e = new grd.EABillGrid();	
		db = new pubpack.EADatabase();
//		throw new Exception(&quot;o:&quot;+o);
		var msg = 0;	
		for(var row=0;row&lt; xmlds.getRowCount();row++)
		{
			var guid = xmlds.getStringAt(row,&quot;guid&quot;);
			var biltyp = xmlds.getStringAt(row,&quot;biltyp&quot;);
			var bilid = xmlds.getStringAt(row,&quot;bilid&quot;);

			if(o==&quot;CHECK&quot;){
				e.CheckOneBill(db,acc,biltyp,bilid,usrid);
//				db.Commit();
			}else if(o==&quot;UNCHECK&quot;){
				e.CheckOneBill(db,acc,biltyp,bilid,usrid,true);
			}else{
				throw new pubpack.EAException(&quot;发现异常操作指示:&quot;+o);
			}

			msg++;
		}
		db.Commit();
		return msg;	
	}catch ( ee ) {
		db.Rollback();
		throw new pubpack.EAException ( ee.toString() );
	}
	finally {
		if (db!=null) db.Close();
	}
}
