var sheet = 0;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SetColVisible(sheet,1,-1);
	_this.SetColVisible(sheet,2,-1);
	_this.SetColVisible(sheet,6,-1);

	if (typeof redraw == &quot;undefined&quot;) refid = invokeOSFunc(&quot;getDeforg&quot;,&quot;&quot;);
	if (refid == &quot;&quot; || refid == null) refid = G_ORGID;
	var sql = &quot;select guid,typ,id,name,note,refid,&apos;编辑欢迎首页&apos;,&apos;编辑功能菜单&apos;,&apos;删除&apos; from PARAM where typ=&apos;&quot;+typ+&quot;&apos; and refid=&apos;&quot;+refid+&quot;&apos; order by id&quot;;
	if (G_NLS == &quot;en&quot;) {
		sql = &quot;select guid,typ,id,name,note,refid,&apos;WELCOME&apos;,&apos;EDIT&apos;,&apos;DELETE&apos; from PARAM where typ=&apos;&quot;+typ+&quot;&apos; and refid=&apos;&quot;+refid+&quot;&apos; order by id&quot;;

		_this.SetCellText(sheet,1,4,&quot;ORG:&quot;);
		_this.SetCellText(sheet,3,3,&quot;URL&quot;);
		_this.SetCellText(sheet,3,4,&quot;NAME&quot;);
		_this.SetCellText(sheet,3,5,&quot;NOTE&quot;);
	}

	var ds = _this.QuerySQL(sql);
	_this.SetMainCellRange(sheet,4,1,6,8);
	_this.SetXmlDS(sheet,4,1,6,9,ds);

	_this.XMLDS_Parse(ds);
	if (_this.XMLDS_GetRowCount() &lt; 3) {
		for (var row = 4; row &lt; _this.GetRowCount(sheet); row ++) {
			if (_this.GetCellText(sheet,row,2) == &quot;&quot;) {
				_this.SetCellText(sheet,row,2,typ);
				_this.SetCellText(sheet,row,6,refid);
				_this.SetCellText(sheet,row,7,&quot;编辑欢迎首页&quot;);
				_this.SetCellText(sheet,row,8,&quot;编辑功能菜单&quot;);
				_this.SetCellText(sheet,row,9,&quot;删除&quot;);
			}
		}
	}

	var list1 =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_ORG&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	_this.SetToComboBox(&quot;&quot;,sheet,1,5,list1);
	_this.SetCellText(sheet,1,5,refid);

	for (var row = 4; row &lt; _this.GetRowCount(sheet); row ++) {
		_this.AutoFixOneRowHeight(sheet,row);
	}
	for (var col = 1; col &lt; _this.GetColCount(sheet); col ++) {
		_this.SetColName(sheet,col,&quot;COL&quot;+col);
	}
	//_this.SplitSheet(&quot;0&quot;, &quot;V&quot;, &quot;50%&quot;);
	/*var layoutxml=&quot;&lt;?xml version = &apos;1.0&apos; encoding=\&quot;GBK\&quot;?&gt; &quot;+
		&quot;&lt;ROWSET&gt;&quot;+
		&quot;&lt;ROW &gt;&quot;+
		 &quot;&lt;C0  height=\&quot;69%\&quot; width=\&quot;59%\&quot; ROWSPAN=\&quot;1\&quot; COLSPAN=\&quot;1\&quot;&gt;0&lt;/C0&gt;&quot;+
		 &quot;&lt;C1  height=\&quot;99%\&quot; width=\&quot;40%\&quot; ROWSPAN=\&quot;2\&quot; COLSPAN=\&quot;1\&quot;&gt;3,4&lt;/C1&gt;&quot;+
		&quot;&lt;/ROW&gt;&quot;+
		&quot;&lt;ROW &gt;&quot;+
		 &quot;&lt;C0  height=\&quot;29%\&quot; width=\&quot;59%\&quot; ROWSPAN=\&quot;1\&quot; COLSPAN=\&quot;1\&quot;&gt;1,2&lt;/C0&gt;&quot;+
		&quot;&lt;/ROW&gt;&quot;+
		&quot;&lt;/ROWSET&gt;&quot;;

	_this.SetLayoutDS(layoutxml);
	*/

	var list2 = _this.CreateListValue();
	_this.SetListValue(list2,&quot;&quot;,&quot;中文（默认）&quot;);
	_this.SetListValue(list2,&quot;en&quot;,&quot;英文&quot;);
	_this.SetToComboBox(&quot;&quot;,sheet,1,8,list2);

	_this.AutoFixScreenWidth();
}

//鼠标左键点击
function _thisOnMouseDClick(sheet,row,col)
{
	if (row &gt; 3 && col == 7) {	//WP8风格编辑
		var org = _this.GetCellText(sheet,1,5);
		var nls = _this.GetCellText(sheet,1,8);
		var id = _this.GetCellText(sheet,row,3);
		var val = &quot;&quot;;

		val = id.substring(id.indexOf(&quot;Entry.jsp?menu=&quot;)+&quot;Entry.jsp?menu=&quot;.length);
		if (val.indexOf(&quot;&&quot;) != -1) val = val.substring(0,val.indexOf(&quot;&&quot;));
		openWindow( &quot;show.sp?grdid=swfwelcome&candel=0&myorgid=&quot;+org+&quot;&layoutid=&quot;+val+&quot;&nls=&quot;+nls);
//		http://3abill.com/bill/ROOT_3ABill/show.sp?grdid=swfwelcome&candel=0&myorgid=3ABill&layoutid=EBILL/Layout.sp?id=mydesktop&nls=
//		openWindow( &quot;show.sp?grdid=swfwelcome&candel=0&myorgid=&quot;+org+&quot;&layoutid=&quot;+val+&quot;/Layout.sp?id=mydesktop&nls=&quot;+nls);
	}
	if (row &gt; 3 && col == 8) {
		var org = _this.GetCellText(sheet,1,5);
		var nls = _this.GetCellText(sheet,1,8);
		var id = _this.GetCellText(sheet,row,3);
		var val = &quot;&quot;;

		if (id.indexOf(&quot;Layout.sp?id=&quot;) != -1) {	//Layout.sp?id=MAIN
			val = id.substring(id.indexOf(&quot;Layout.sp?id=&quot;)+&quot;Layout.sp?id=&quot;.length);
			if (val.indexOf(&quot;&&quot;) != -1) val = val.substring(0,val.indexOf(&quot;&&quot;));

			openWindow( &quot;show.sp?grdid=LayoutMain&candel=0&myorgid=&quot;+org+&quot;&layoutid=&quot;+val);
		} else if (id.indexOf(&quot;MyCloud.jsp?menu=&quot;) != -1) { //MyCloud.jsp?menu=SPACEVAPP
			val = id.substring(id.indexOf(&quot;MyCloud.jsp?menu=&quot;)+&quot;MyCloud.jsp?menu=&quot;.length);
			if (val.indexOf(&quot;&&quot;) != -1) val = val.substring(0,val.indexOf(&quot;&&quot;));

			openWindow( &quot;show.sp?grdid=SYSURLED&grdtyp=Q&myorgid=&quot;+org+&quot;&menu=&quot;+val+&quot;&loaddata=1&quot; );
		} else if (id.indexOf(&quot;Entry.jsp?menu=&quot;) != -1) { //Entry.jsp?menu=SPACEVAPP
			val = id.substring(id.indexOf(&quot;Entry.jsp?menu=&quot;)+&quot;Entry.jsp?menu=&quot;.length);
			if (val.indexOf(&quot;&&quot;) != -1) val = val.substring(0,val.indexOf(&quot;&&quot;));

			openWindow( &quot;show.sp?grdid=SYSURLED&grdtyp=Q&myorgid=&quot;+org+&quot;&menu=&quot;+val+&quot;&loaddata=1&quot; );
		} else if (id.indexOf(&quot;EntryCloud.jsp?menu=&quot;) != -1) { //EntryCloud.jsp?menu=SPACEVAPP
			val = id.substring(id.indexOf(&quot;EntryCloud.jsp?menu=&quot;)+&quot;EntryCloud.jsp?menu=&quot;.length);
			if (val.indexOf(&quot;&&quot;) != -1) val = val.substring(0,val.indexOf(&quot;&&quot;));

			openWindow( &quot;show.sp?grdid=SYSURLED&grdtyp=Q&myorgid=&quot;+org+&quot;&menu=&quot;+val+&quot;&loaddata=1&quot; );
		}
	}
	if (row &gt; 3 && col == 9) {
		var guid = _this.GetCellText(sheet,row,1);
		var name = _this.GetCellText(sheet,row,4);

		if (window.confirm(&quot;是否确定删除 &quot;+name+&quot; ?&quot;)) {
			//POST方式调用网络OS函数
			var param = new Object();
			param.guid    = guid;
			try {
				var msg = invokeOSFuncExt(&quot;delt&quot;,param);
				alert ( msg );
				try { window.opener.lchref(); } catch ( ee ) { }
				window.location.href = window.location.href;
			}
			catch (e) {
				alert ( e );
			}
		}
	}
}

//插入
function inst()
{
	var row = _this.AppendRow(sheet,_this.GetRowCount(sheet)-1);
	_this.SetCellText(sheet,row,2,typ);
	_this.SetCellText(sheet,row,6,_this.GetCellText(sheet,1,5));
	_this.SetCellText(sheet,row,7,&quot;编辑欢迎首页&quot;);
	_this.SetCellText(sheet,row,8,&quot;编辑功能菜单&quot;);
	_this.SetCellText(sheet,row,9,&quot;删除&quot;);
}

//保存
function save()
{
	//POST方式调用网络OS函数
	var param = new Object();
	param.xmlstr = _this.GetXml(sheet,4,1,_this.GetRowCount(sheet)-1,8);
	try {
		var msg = invokeOSFuncExt(&quot;save&quot;,param);
		alert ( msg );
		try { window.opener.lchref(); } catch ( ee ) { }
		window.location.href = window.location.href;
	}
	catch (e) {
		alert ( e );
	}
}

//修改单元格内容
