	  	_this.SetRowVisible(sheet,17,-1);
	  	_this.SetRowVisible(sheet,11,-1);


	 } 
	 _this.SetRowVisible(sheet,18,-1);
	for ( var i=1;i&lt;_this.GetRowCount(0)-2;i++) {
		_this.SetCellBkColor(sheet,i,1,0xEE,0xEE,0xEE);
	}
	if ( stat == &quot;0&quot; ) {
